                                onClick={() => resendInvitationMutation.mutate(invitation.id)}
                              >
                                <Send className="h-4 w-4" />
                              </Button>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => cancelInvitationMutation.mutate(invitation.id)}
