      const batchSize = 5;
      for (let i = 0; i < callLogsWithoutAudio.length; i += batchSize) {
        const batch = callLogsWithoutAudio.slice(i, i + batchSize);
        await Promise.all(batch.map(processCall));
      }

      res.json({
        message: "Batch fetch completed",
        results,
