                />
              </div>
              <div>
                <Label>Per Minute Rate ($)</Label>
                <Input
                  type="number"
                  step="0.01"
                  value={newPackage.perMinuteRate}
                  onChange={(e) => setNewPackage({ ...newPackage, perMinuteRate: e.target.value })}
                  data-testid="input-package-minute-rate"
