              <div>
                <Label>Max Users</Label>
                <Input
                  type="number"
                  value={newPackage.maxUsers}
                  onChange={(e) => setNewPackage({ ...newPackage, maxUsers: e.target.value })}
                  data-testid="input-package-max-users"
