    const [agent] = await db()
      .update(agents)
      .set({ 
        organizationId: newOrganizationId,
        updatedAt: new Date() 
      })
      .where(eq(agents.id, agentId))
      .returning();

    // Also clear any user assignments for this agent since it's moving to a new org
    await db()
      .delete(userAgents)
      .where(eq(userAgents.agentId, agentId));

    return agent;
  }

