import { useState, useEffect, useMemo, useCallback } from "react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Search, X, Clock, User, Bot, Zap, RefreshCw } from "lucide-react";
import type { CallLog } from "@shared/schema";

interface TranscriptSearchProps {
  callLogs: CallLog[];
  onSearchResults: (results: CallLog[]) => void;
  onClearSearch: () => void;
}

interface SearchResult {
  callLog: CallLog;
  matches: Array<{
    speaker: string;
    text: string;
    timestamp?: string;
    highlightedText: string;
  }>;
  relevanceScore: number;
}

export function TranscriptSearch({ callLogs, onSearchResults, onClearSearch }: TranscriptSearchProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [isSearching, setIsSearching] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);

  // Parse transcript into structured format
  const parseTranscript = useCallback((transcript: any) => {
    if (!transcript) return [];

    if (Array.isArray(transcript)) {
      return transcript.map((turn: any, index: number) => ({
        id: index,
        speaker: turn.role || turn.speaker || 'unknown',
        text: turn.message || turn.content || turn.text || '',
        timestamp: turn.timestamp || turn.time_in_call_secs || null
      }));
    } else if (typeof transcript === 'string') {
      try {
        const parsed = JSON.parse(transcript);
        if (Array.isArray(parsed)) {
          return parsed.map((turn: any, index: number) => ({
            id: index,
            speaker: turn.role || turn.speaker || 'unknown',
            text: turn.message || turn.content || turn.text || '',
            timestamp: turn.timestamp || turn.time_in_call_secs || null
          }));
        }
      } catch {
        // If parsing fails, treat as plain text
        return [{
          id: 0,
          speaker: 'unknown',
          text: transcript,
          timestamp: null
        }];
      }
    }

    return [];
  }, []);

  // Highlight search terms in text
  const highlightText = useCallback((text: string, query: string) => {
    if (!query.trim()) return text;

    const regex = new RegExp(`(${query.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')})`, 'gi');
    const parts = text.split(regex);

    return parts.map((part, index) => 
      regex.test(part) ? (
        <mark key={index} className="bg-yellow-200 dark:bg-yellow-800 px-1 rounded">
          {part}
        </mark>
      ) : part
    );
  }, []);

  // Enhanced search with relevance scoring and highlighting
  const searchTranscripts = useCallback((query: string): SearchResult[] => {
    if (!query.trim()) {
      return callLogs.map(callLog => ({
        callLog,
        matches: [],
        relevanceScore: 0
      }));
    }

    const searchTerms = query.toLowerCase().split(/\s+/).filter(term => term.length > 0);
    const results: SearchResult[] = [];

    callLogs.forEach(callLog => {
      if (!callLog.transcript) return;

      const transcriptEntries = parseTranscript(callLog.transcript);
      const matches: SearchResult['matches'] = [];
      let relevanceScore = 0;

      transcriptEntries.forEach(entry => {
        const text = entry.text.toLowerCase();
        let entryScore = 0;
        let hasMatch = false;

        searchTerms.forEach(term => {
          const termRegex = new RegExp(term.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'), 'gi');
          const matches_found = text.match(termRegex);

          if (matches_found) {
            hasMatch = true;
            // Score based on number of matches and position
            entryScore += matches_found.length * 10;

            // Boost score for exact phrase matches
            if (text.includes(query.toLowerCase())) {
              entryScore += 50;
            }

            // Boost score for beginning of text
            const firstMatchIndex = text.indexOf(term);
            if (firstMatchIndex < 50) {
              entryScore += 20;
            }
          }
        });

        if (hasMatch) {
          relevanceScore += entryScore;
          matches.push({
            speaker: entry.speaker,
            text: entry.text,
            timestamp: entry.timestamp,
            highlightedText: highlightText(entry.text, query) as any
          });
        }
      });

      if (matches.length > 0) {
        results.push({
          callLog,
          matches,
          relevanceScore
        });
      }
    });

    // Sort by relevance score (highest first)
    return results.sort((a, b) => b.relevanceScore - a.relevanceScore);
  }, [callLogs, parseTranscript, highlightText]);

  // Handle search with enhanced results
  const handleSearch = useCallback(() => {
    if (!searchQuery.trim()) {
      setSearchResults([]);
      onClearSearch();
      return;
    }

    setIsSearching(true);

    try {
      const results = searchTranscripts(searchQuery);
      setSearchResults(results);
      onSearchResults(results.map(r => r.callLog));
    } catch (error) {
      console.error('Search error:', error);
      setSearchResults([]);
      onSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  }, [searchQuery, searchTranscripts, onSearchResults, onClearSearch]);

  // Clear search
  const handleClear = useCallback(() => {
    setSearchQuery("");
    setSearchResults([]);
    setIsSearching(false);
    onClearSearch();
  }, [onClearSearch]);

  // Auto-search as user types (with debounce)
  useEffect(() => {
    const timer = setTimeout(() => {
      handleSearch();
    }, 300); // Reduced debounce time for better UX

    return () => clearTimeout(timer);
  }, [searchQuery, handleSearch]);

  return (
    <div className="space-y-4">
      <div className="flex gap-2 items-center">
        <div className="relative flex-1 max-w-md">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
          <Input
            type="text"
            placeholder="Search in transcripts..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
            className="pl-10 pr-10"
            data-testid="input-transcript-search"
          />
          {searchQuery && (
            <button
              onClick={handleClear}
              className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
              data-testid="button-clear-search"
            >
              <X className="w-4 h-4" />
            </button>
          )}
        </div>
        <Button
          onClick={handleSearch}
          disabled={isSearching || !searchQuery.trim()}
          variant="secondary"
          data-testid="button-search-transcripts"
        >
          {isSearching ? (
            <>
              <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
              Searching...
            </>
          ) : (
            <>
              <Zap className="w-4 h-4 mr-2" />
              Search
            </>
          )}
        </Button>
        {searchQuery && searchResults.length !== callLogs.length && (
          <div className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400">
            <Badge variant="secondary">
              {searchResults.length} result{searchResults.length !== 1 ? 's' : ''}
            </Badge>
            {searchResults.length > 0 && (
              <Badge variant="outline">
                Relevance: {Math.round(searchResults[0]?.relevanceScore || 0)}
              </Badge>
            )}
          </div>
        )}
      </div>

      {/* Search Results Preview */}
      {searchQuery && searchResults.length > 0 && (
        <div className="bg-gray-50 dark:bg-gray-800 rounded-lg p-4 space-y-3">
          <div className="flex items-center gap-2 text-sm font-medium text-gray-700 dark:text-gray-300">
            <Search className="w-4 h-4" />
            Search Results Preview
          </div>

          {searchResults.slice(0, 3).map((result, index) => (
            <div key={result.callLog.id} className="bg-white dark:bg-gray-700 rounded border p-3">
              <div className="flex items-center justify-between mb-2">
                <div className="flex items-center gap-2">
                  <Badge variant="outline" className="text-xs">
                    Call #{result.callLog.id.slice(-6)}
                  </Badge>
                  <Badge variant="secondary" className="text-xs">
                    {result.matches.length} match{result.matches.length !== 1 ? 'es' : ''}
                  </Badge>
                </div>
                <div className="flex items-center gap-1 text-xs text-gray-500">
                  <Clock className="w-3 h-3" />
                  {result.callLog.duration ? `${Math.floor(result.callLog.duration / 60)}m ${result.callLog.duration % 60}s` : 'N/A'}
                </div>
              </div>

              {result.matches.slice(0, 2).map((match, matchIndex) => (
                <div key={matchIndex} className="text-sm space-y-1">
                  <div className="flex items-center gap-2">
                    {match.speaker === 'user' || match.speaker === 'customer' ? (
                      <User className="w-3 h-3 text-blue-500" />
                    ) : (
                      <Bot className="w-3 h-3 text-green-500" />
                    )}
                    <span className="font-medium text-gray-600 dark:text-gray-400 capitalize">
                      {match.speaker}
                    </span>
                    {match.timestamp && (
                      <span className="text-xs text-gray-400">
                        {match.timestamp}s
                      </span>
                    )}
                  </div>
                  <div className="text-gray-700 dark:text-gray-300 ml-5">
                    {match.highlightedText}
                  </div>
                </div>
              ))}

              {result.matches.length > 2 && (
                <div className="text-xs text-gray-500 mt-2">
                  +{result.matches.length - 2} more matches
                </div>
              )}
            </div>
          ))}

          {searchResults.length > 3 && (
            <div className="text-center text-sm text-gray-500">
              And {searchResults.length - 3} more results...
            </div>
          )}
        </div>
      )}
    </div>
  );
}

client/src/components/knowledge-base/kno.content:
/**
 * Knowledge Base Manager Component
 * 
 * Provides interface for managing knowledge base entries and integrating with voice agents
 */

import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { Search, Plus, BookOpen, Brain, MessageSquare, Lightbulb, Tag, Upload, Settings } from "lucide-react";
import { DocumentUpload } from "./document-upload";

interface KnowledgeBaseEntry {
  id: string;
  title: string;
  content: string;
  category: string;
  tags: string[];
  createdAt: string;
  updatedAt: string;
}

interface KnowledgeBaseResponse {
  answer: string;
  sources: KnowledgeBaseEntry[];
  confidence: number;
  followUpQuestions?: string[];
}

export function KnowledgeBaseManager() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState<string>("all");
  const [newEntry, setNewEntry] = useState({
    title: "",
    content: "",
    category: "General",
    tags: ""
  });
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Search knowledge base
  const searchMutation = useMutation({
    mutationFn: async (query: string) => {
      const response = await fetch("/api/knowledge-base/search", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ query, category: selectedCategory !== "all" ? selectedCategory : undefined })
      });

      if (!response.ok) {
        throw new Error("Search failed");
      }

      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Search Complete",
        description: `Found ${data.data.sources.length} relevant sources`,
      });
    },
    onError: (error) => {
      toast({
        title: "Search Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Add knowledge base entry
  const addEntryMutation = useMutation({
    mutationFn: async (entry: typeof newEntry) => {
      const response = await fetch("/api/knowledge-base/entries", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({
          title: entry.title,
          content: entry.content,
          category: entry.category,
          tags: entry.tags.split(",").map(tag => tag.trim()).filter(tag => tag)
        })
      });

      if (!response.ok) {
        throw new Error("Failed to add entry");
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Entry Added",
        description: "Knowledge base entry added successfully",
      });
      setIsAddDialogOpen(false);
      setNewEntry({ title: "", content: "", category: "General", tags: "" });
      queryClient.invalidateQueries({ queryKey: ["knowledge-base"] });
    },
    onError: (error) => {
      toast({
        title: "Add Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSearch = () => {
    if (searchQuery.trim()) {
      searchMutation.mutate(searchQuery);
    }
  };

  const handleAddEntry = () => {
    if (newEntry.title.trim() && newEntry.content.trim()) {
      addEntryMutation.mutate(newEntry);
    }
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Brain className="w-5 h-5" />
            Knowledge Base Manager
          </CardTitle>
          <CardDescription>
            Manage your organization's knowledge base and integrate it with voice agents
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Tabs defaultValue="search" className="w-full">
            <TabsList className="grid w-full grid-cols-4">
              <TabsTrigger value="search">Search</TabsTrigger>
              <TabsTrigger value="add">Add Entry</TabsTrigger>
              <TabsTrigger value="upload">Upload Documents</TabsTrigger>
              <TabsTrigger value="integrate">Integrate with Agents</TabsTrigger>
            </TabsList>

            <TabsContent value="search" className="space-y-4">
              <div className="flex gap-2">
                <Input
                  placeholder="Search knowledge base..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  onKeyPress={(e) => e.key === "Enter" && handleSearch()}
                />
                <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                  <SelectTrigger className="w-40">
                    <SelectValue placeholder="Category" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Categories</SelectItem>
                    <SelectItem value="Technical">Technical</SelectItem>
                    <SelectItem value="Best Practices">Best Practices</SelectItem>
                    <SelectItem value="Troubleshooting">Troubleshooting</SelectItem>
                    <SelectItem value="Product Information">Product Information</SelectItem>
                    <SelectItem value="User Guides">User Guides</SelectItem>
                  </SelectContent>
                </Select>
                <Button onClick={handleSearch} disabled={searchMutation.isPending}>
                  <Search className="w-4 h-4 mr-2" />
                  {searchMutation.isPending ? "Searching..." : "Search"}
                </Button>
              </div>

              {searchMutation.data && (
                <Card>
                  <CardHeader>
                    <CardTitle className="text-lg">Search Results</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div className="p-4 bg-blue-50 rounded-lg">
                      <h4 className="font-medium mb-2">Answer:</h4>
                      <p className="text-sm text-gray-700">
                        {searchMutation.data.data.answer}
                      </p>
                      <div className="mt-2 flex items-center gap-2">
                        <Badge variant="outline">
                          Confidence: {Math.round(searchMutation.data.data.confidence * 100)}%
                        </Badge>
                      </div>
                    </div>

                    {searchMutation.data.data.sources.length > 0 && (
                      <div>
                        <h4 className="font-medium mb-2">Sources:</h4>
                        <div className="space-y-2">
                          {searchMutation.data.data.sources.map((source: KnowledgeBaseEntry) => (
                            <Card key={source.id} className="p-3">
                              <div className="flex items-start justify-between">
                                <div>
                                  <h5 className="font-medium">{source.title}</h5>
                                  <p className="text-sm text-gray-600 mt-1">
                                    {source.content.substring(0, 200)}...
                                  </p>
                                  <div className="flex gap-2 mt-2">
                                    <Badge variant="secondary">{source.category}</Badge>
                                    {source.tags.map(tag => (
                                      <Badge key={tag} variant="outline" className="text-xs">
                                        <Tag className="w-3 h-3 mr-1" />
                                        {tag}
                                      </Badge>
                                    ))}
                                  </div>
                                </div>
                              </div>
                            </Card>
                          ))}
                        </div>
                      </div>
                    )}

                    {searchMutation.data.data.followUpQuestions && (
                      <div>
                        <h4 className="font-medium mb-2">Suggested Follow-up Questions:</h4>
                        <div className="space-y-1">
                          {searchMutation.data.data.followUpQuestions.map((question: string, index: number) => (
                            <Button
                              key={index}
                              variant="ghost"
                              size="sm"
                              className="text-left justify-start h-auto p-2"
                              onClick={() => setSearchQuery(question)}
                            >
                              <Lightbulb className="w-3 h-3 mr-2" />
                              {question}
                            </Button>
                          ))}
                        </div>
                      </div>
                    )}
                  </CardContent>
                </Card>
              )}
            </TabsContent>

            <TabsContent value="add" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Plus className="w-4 h-4" />
                    Add New Knowledge Entry
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div>
                    <Label htmlFor="title">Title</Label>
                    <Input
                      id="title"
                      placeholder="Enter entry title..."
                      value={newEntry.title}
                      onChange={(e) => setNewEntry({ ...newEntry, title: e.target.value })}
                    />
                  </div>

                  <div>
                    <Label htmlFor="category">Category</Label>
                    <Select value={newEntry.category} onValueChange={(value) => setNewEntry({ ...newEntry, category: value })}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="General">General</SelectItem>
                        <SelectItem value="Technical">Technical</SelectItem>
                        <SelectItem value="Best Practices">Best Practices</SelectItem>
                        <SelectItem value="Troubleshooting">Troubleshooting</SelectItem>
                        <SelectItem value="Product Information">Product Information</SelectItem>
                        <SelectItem value="User Guides">User Guides</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <Label htmlFor="tags">Tags (comma-separated)</Label>
                    <Input
                      id="tags"
                      placeholder="voice, agents, api..."
                      value={newEntry.tags}
                      onChange={(e) => setNewEntry({ ...newEntry, tags: e.target.value })}
                    />
                  </div>

                  <div>
                    <Label htmlFor="content">Content</Label>
                    <Textarea
                      id="content"
                      placeholder="Enter the knowledge base content..."
                      rows={6}
                      value={newEntry.content}
                      onChange={(e) => setNewEntry({ ...newEntry, content: e.target.value })}
                    />
                  </div>

                  <Button 
                    onClick={handleAddEntry} 
                    disabled={addEntryMutation.isPending || !newEntry.title.trim() || !newEntry.content.trim()}
                    className="w-full"
                  >
                    <Plus className="w-4 h-4 mr-2" />
                    {addEntryMutation.isPending ? "Adding..." : "Add Entry"}
                  </Button>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="upload" className="space-y-4">
              <DocumentUpload />
            </TabsContent>

            <TabsContent value="integrate" className="space-y-4">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <MessageSquare className="w-4 h-4" />
                    Integrate with Voice Agents
                  </CardTitle>
                  <CardDescription>
                    Enhance your voice agents with knowledge base capabilities
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="space-y-6">
                      <div className="p-4 bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-900/20 dark:to-purple-900/20 rounded-lg border border-blue-200 dark:border-blue-800">
                        <h4 className="font-medium text-blue-800 dark:text-blue-200 mb-3 flex items-center gap-2">
                          <Brain className="w-5 h-5" />
                          Knowledge Base Integration Benefits
                        </h4>
                        <ul className="text-sm text-blue-700 dark:text-blue-300 space-y-2">
                          <li className="flex items-start gap-2">
                            <div className="w-1.5 h-1.5 bg-blue-500 rounded-full mt-2 flex-shrink-0"></div>
                            Voice agents can answer questions from your knowledge base
                          </li>
                          <li className="flex items-start gap-2">
                            <div className="w-1.5 h-1.5 bg-blue-500 rounded-full mt-2 flex-shrink-0"></div>
                            Natural language understanding for complex queries
                          </li>
                          <li className="flex items-start gap-2">
                            <div className="w-1.5 h-1.5 bg-blue-500 rounded-full mt-2 flex-shrink-0"></div>
                            Automatic source citation and follow-up suggestions
                          </li>
                          <li className="flex items-start gap-2">
                            <div className="w-1.5 h-1.5 bg-blue-500 rounded-full mt-2 flex-shrink-0"></div>
                            Multilingual support with ElevenLabs models
                          </li>
                        </ul>
                      </div>

                      <div className="p-4 bg-gradient-to-r from-emerald-50 to-teal-50 dark:from-emerald-900/20 dark:to-teal-900/20 rounded-lg border border-emerald-200 dark:border-emerald-800">
                        <h4 className="font-medium text-emerald-800 dark:text-emerald-200 mb-3 flex items-center gap-2">
                          <Settings className="w-5 h-5" />
                          How to Configure Agents
                        </h4>
                        <div className="text-sm text-emerald-700 dark:text-emerald-300 space-y-2">
                          <p>To enhance your voice agents with knowledge base capabilities:</p>
                          <ol className="list-decimal list-inside space-y-1 ml-4">
                            <li>Go to the <strong>Agent Settings</strong> tab in the navigation</li>
                            <li>Select an agent from the dropdown</li>
                            <li>Navigate to the <strong>Knowledge</strong> tab</li>
                            <li>Configure knowledge base integration settings</li>
                          </ol>
                        </div>
                      </div>

                      <div className="text-center py-6">
                        <div className="inline-flex items-center gap-2 px-4 py-2 bg-gradient-to-r from-primary/10 to-purple-500/10 rounded-full border border-primary/20">
                          <BookOpen className="w-5 h-5 text-primary" />
                          <span className="text-sm font-medium text-primary">
                            Knowledge Base Ready for Integration
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>
    </div>
  );
}

