    admin: {
      label: "Admin",
      description: "Manage users and settings",
      permissions: [
        "view_analytics", "view_call_history", "manage_agents", "configure_tools", 
        "access_playground", "manage_voices", "manage_phone_numbers", "manage_users", "manage_settings"
      ],
