        }

        .status-item {
          display: flex;
          justify-content: space-between;
          margin-bottom: 8px;
        }

        .status-item:last-child {
          margin-bottom: 0;
        }

        .label {
          font-weight: 600;
          color: #4a5568;
        }

        .value {
          font-weight: 500;
        }

        .value.success {
          color: #38a169;
        }

        .value.error {
          color: #e53e3e;
        }

        .api-key-input {
          display: flex;
          gap: 10px;
          align-items: center;
        }

        .api-key-field {
          flex: 1;
          padding: 10px;
          border: 1px solid #e2e8f0;
          border-radius: 4px;
          font-size: 14px;
        }

        .setup-btn {
          padding: 10px 20px;
          background: #3182ce;
