        }

        .credits-btn {
          border-color: #d69e2e;
          color: #d69e2e;
        }

        .dashboard-btn {
          border-color: #805ad5;
          color: #805ad5;
        }

        .calls-btn {
          border-color: #e53e3e;
          color: #e53e3e;
        }

        .analytics-btn {
          border-color: #319795;
          color: #319795;
        }

        .result-card {
          padding: 20px;
          border-radius: 6px;
          border: 1px solid #e2e8f0;
        }

        .result-card.success {
          background: #f0fff4;
          border-color: #9ae6b4;
        }

        .result-card.error {
          background: #fed7d7;
          border-color: #feb2b2;
        }

        .result-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 15px;
          flex-wrap: wrap;
          gap: 10px;
        }

        .status {
          font-weight: 600;
        }

        .duration,
        .timestamp {
          font-size: 14px;
          color: #718096;
        }

        .result-message {
          margin-bottom: 15px;
          font-weight: 500;
        }

        .result-errors {
          margin-bottom: 15px;
        }

        .result-errors h4 {
          color: #e53e3e;
          margin-bottom: 8px;
        }

        .result-errors ul {
          margin: 0;
          padding-left: 20px;
        }

        .result-errors li {
          color: #e53e3e;
          margin-bottom: 4px;
        }

        .result-data {
          margin-top: 15px;
        }

        .result-data h4 {
          color: #2d3748;
          margin-bottom: 8px;
        }

        .result-data pre {
          background: #f7fafc;
          padding: 15px;
          border-radius: 4px;
          overflow-x: auto;
          font-size: 12px;
          border: 1px solid #e2e8f0;
        }

        @media (max-width: 768px) {
          .sync-buttons {
            grid-template-columns: 1fr;
          }

          .api-key-input {
            flex-direction: column;
          }

          .result-header {
            flex-direction: column;
            align-items: flex-start;
          }
        }
      `}</style>
    </div>
  );
};

export default RealtimeSyncPanel;

