/**
 * WebSocket Routes for Real-time Sync
 * 
 * Handles WebSocket connections for real-time dashboard updates
 */

import { Express, Request, Response } from "express";
import { WebSocketServer, WebSocket } from 'ws';
import { realtimeSyncService } from './services/realtime-sync';

// Define isAuthenticated middleware locally (same as in routes.ts)
const isAuthenticated = (req: any, res: any, next: any) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ message: "Authentication required" });
  }
  next();
};

export function setupWebSocketRoutes(app: Express, server: any) {
  // Create WebSocket server
  const wss = new WebSocketServer({ 
    server,
    path: '/ws/realtime-sync'
  });

  console.log('[WEBSOCKET] Real-time sync WebSocket server started');

  wss.on('connection', async (ws, req) => {
    console.log('[WEBSOCKET] New connection attempt');

    try {
      // Extract user info from query parameters or headers
      const url = new URL(req.url!, `http://${req.headers.host}`);
      const token = url.searchParams.get('token') || req.headers.authorization?.replace('Bearer ', '');

      if (!token) {
        console.log('[WEBSOCKET] No authentication token provided');
        ws.close(1008, 'Authentication required');
        return;
      }

      // For now, we'll use a simple approach - in production you'd verify the JWT token
      // This is a simplified version - you might want to implement proper JWT verification
      const userId = url.searchParams.get('userId');
      const organizationId = url.searchParams.get('organizationId');

      if (!userId || !organizationId) {
        console.log('[WEBSOCKET] Missing userId or organizationId');
        ws.close(1008, 'Missing user or organization information');
        return;
      }

      // Register the client for real-time updates
      realtimeSyncService.registerClient(ws, organizationId, userId);

      console.log(`[WEBSOCKET] Client connected: ${organizationId}-${userId}`);

      // Send welcome message
      ws.send(JSON.stringify({
        type: 'connected',
        data: {
          message: 'Connected to real-time sync',
          organizationId,
          userId,
          timestamp: new Date().toISOString()
        }
      }));

      // Handle incoming messages
      ws.on('message', (data) => {
        try {
          const message = JSON.parse(data.toString());

          switch (message.type) {
            case 'ping':
              ws.send(JSON.stringify({
                type: 'pong',
                data: { timestamp: new Date().toISOString() }
              }));
              break;

            case 'sync_request':
              // Handle sync request from client
              handleSyncRequest(organizationId, userId, message.data, ws);
              break;

            default:
              console.log('[WEBSOCKET] Unknown message type:', message.type);
          }
        } catch (error) {
          console.error('[WEBSOCKET] Error processing message:', error);
        }
      });

      ws.on('close', () => {
        console.log(`[WEBSOCKET] Client disconnected: ${organizationId}-${userId}`);
      });

      ws.on('error', (error) => {
        console.error(`[WEBSOCKET] Connection error for ${organizationId}-${userId}:`, error);
      });

    } catch (error) {
      console.error('[WEBSOCKET] Error setting up connection:', error);
      ws.close(1011, 'Internal server error');
    }
  });

  return wss;
}

/**
 * Handle sync request from WebSocket client
 */
async function handleSyncRequest(organizationId: string, userId: string, data: any, ws: WebSocket) {
  try {
    console.log(`[WEBSOCKET] Sync request from ${organizationId}-${userId}`);

    // Check if sync is already in progress
    if (realtimeSyncService.isSyncInProgress(organizationId)) {
      ws.send(JSON.stringify({
        type: 'sync_error',
        data: {
          error: 'Sync already in progress',
          timestamp: new Date().toISOString()
        }
      }));
      return;
    }

    // Start real-time sync
    const result = await realtimeSyncService.startRealtimeSync(organizationId, data.agentId);

    // Send success confirmation
    ws.send(JSON.stringify({
      type: 'sync_success',
      data: {
        result,
        timestamp: new Date().toISOString()
      }
    }));

  } catch (error: any) {
    console.error(`[WEBSOCKET] Sync request failed for ${organizationId}-${userId}:`, error);

    ws.send(JSON.stringify({
      type: 'sync_error',
      data: {
        error: error.message,
        timestamp: new Date().toISOString()
      }
    }));
  }
}

/**
 * Setup HTTP endpoints for WebSocket connection info
 */
export function setupWebSocketEndpoints(app: Express) {
  // Get WebSocket connection info
  app.get('/api/websocket/info', isAuthenticated, async (req: any, res) => {
    try {
      const user = req.user;
      const organizationId = user.organizationId;

      const connectionInfo = {
        wsUrl: `/ws/realtime-sync?token=${user.id}&userId=${user.id}&organizationId=${organizationId}`,
        connectedClients: realtimeSyncService.getConnectedClientsCount(organizationId),
        syncInProgress: realtimeSyncService.isSyncInProgress(organizationId)
      };

      res.json(connectionInfo);
    } catch (error: any) {
      console.error('[WEBSOCKET] Error getting connection info:', error);
      res.status(500).json({ error: error.message });
    }
  });

  // Trigger real-time sync via HTTP (alternative to WebSocket)
  app.post('/api/websocket/sync', isAuthenticated, async (req: any, res) => {
    try {
      const user = req.user;
      const organizationId = user.organizationId;
      const { agentId } = req.body;

      console.log(`[WEBSOCKET] HTTP sync request from user ${user.id}`);

      // Start real-time sync
      const result = await realtimeSyncService.startRealtimeSync(organizationId, agentId);

      res.json({
        success: true,
        message: 'Real-time sync completed',
        result,
        timestamp: new Date().toISOString()
      });

    } catch (error: any) {
      console.error('[WEBSOCKET] HTTP sync request failed:', error);
      res.status(500).json({
        success: false,
        error: error.message,
        timestamp: new Date().toISOString()
      });
    }
  });
}

client/src/components/modals/add-agent-m.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Form, FormControl, FormDescription, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Bot, Upload, Sparkles, Wand2 } from "lucide-react";

const importAgentSchema = z.object({
  elevenLabsAgentId: z.string().min(1, "Voice Agent ID is required"),
  name: z.string().optional(),
});

const createAgentSchema = z.object({
  name: z.string().min(1, "Agent name is required"),
  firstMessage: z.string().min(1, "First message is required"),
  systemPrompt: z.string().min(1, "System prompt is required"),
  language: z.string().default("en"),
  voiceId: z.string().optional(),
});

type ImportAgentForm = z.infer<typeof importAgentSchema>;
type CreateAgentForm = z.infer<typeof createAgentSchema>;

interface AddAgentModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function AddAgentModal({ open, onOpenChange }: AddAgentModalProps) {
  const [isValidating, setIsValidating] = useState(false);
  const [validatedData, setValidatedData] = useState<any>(null);
  const [activeTab, setActiveTab] = useState("import");
  const [isGeneratingPrompt, setIsGeneratingPrompt] = useState(false);
  const [promptDescription, setPromptDescription] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const importForm = useForm<ImportAgentForm>({
    resolver: zodResolver(importAgentSchema),
    defaultValues: {
      elevenLabsAgentId: "",
      name: "",
    },
  });

  const createForm = useForm<CreateAgentForm>({
    resolver: zodResolver(createAgentSchema),
    defaultValues: {
      name: "",
      firstMessage: "Hello! How can I assist you today?",
      systemPrompt: "You are a helpful AI assistant.",
      language: "en",
      voiceId: "",
    },
  });

  const validateAgentMutation = useMutation({
    mutationFn: async (data: { elevenLabsAgentId: string }) => {
      setIsValidating(true);
      const response = await apiRequest("POST", "/api/agents/validate", data);
      return response.json();
    },
    onSuccess: (data) => {
      setValidatedData(data.agentData);
      importForm.setValue("name", data.agentData.name || "");
      toast({
        title: "Agent Validated",
        description: "Agent found and validated successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Validation Failed",
        description: error.message,
        variant: "destructive",
      });
    },
    onSettled: () => {
      setIsValidating(false);
    },
  });

  const importAgentMutation = useMutation({
    mutationFn: async (data: ImportAgentForm) => {
      await apiRequest("POST", "/api/agents", {
        elevenLabsAgentId: data.elevenLabsAgentId,
        name: data.name || validatedData?.name || "Unnamed Agent",
        description: validatedData?.description,
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Agent imported successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/agents"] });
      handleClose();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const createAgentMutation = useMutation({
    mutationFn: async (data: CreateAgentForm) => {
      const response = await apiRequest("POST", "/api/agents/create", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Agent created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/agents"] });
      handleClose();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const generatePromptMutation = useMutation({
    mutationFn: async (description: string) => {
      setIsGeneratingPrompt(true);
      const response = await apiRequest("POST", "/api/agents/generate-prompt", { description });
      return response.json();
    },
    onSuccess: (data) => {
      createForm.setValue("systemPrompt", data.systemPrompt);
      toast({
        title: "Prompt Generated",
        description: "AI has generated a comprehensive system prompt based on your description",
      });
    },
    onError: (error) => {
      toast({
        title: "Generation Failed",
        description: error.message,
        variant: "destructive",
      });
    },
    onSettled: () => {
      setIsGeneratingPrompt(false);
    },
  });

  const handleGeneratePrompt = () => {
    if (!promptDescription.trim() || promptDescription.trim().length < 10) {
      toast({
        title: "Description Too Short",
        description: "Please provide a more detailed description (at least 10 characters)",
        variant: "destructive",
      });
      return;
    }
    generatePromptMutation.mutate(promptDescription);
  };

  const handleClose = () => {
    importForm.reset();
    createForm.reset();
    setValidatedData(null);
    setActiveTab("import");
    setPromptDescription("");
    setIsGeneratingPrompt(false);
    onOpenChange(false);
  };

  const onValidate = () => {
    const elevenLabsAgentId = importForm.getValues("elevenLabsAgentId");
    if (!elevenLabsAgentId) {
      toast({
        title: "Error",
        description: "Please enter a Voice Agent ID",
        variant: "destructive",
      });
      return;
    }
    validateAgentMutation.mutate({ elevenLabsAgentId });
  };

  const onImportSubmit = (data: ImportAgentForm) => {
    if (!validatedData) {
      toast({
        title: "Error",
        description: "Please validate the agent first",
        variant: "destructive",
      });
      return;
    }
    importAgentMutation.mutate(data);
  };

  const onCreateSubmit = (data: CreateAgentForm) => {
    createAgentMutation.mutate(data);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="w-[95vw] max-w-lg max-h-[85vh] flex flex-col">
        <DialogHeader className="flex-shrink-0">
          <DialogTitle data-testid="text-modal-title" className="text-base sm:text-lg">Add New Agent</DialogTitle>
        </DialogHeader>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full flex flex-col flex-1 overflow-hidden">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="import" className="flex items-center gap-2">
              <Upload className="w-4 h-4" />
              Import Existing
            </TabsTrigger>
            <TabsTrigger value="create" className="flex items-center gap-2">
              <Bot className="w-4 h-4" />
              Create New
            </TabsTrigger>
          </TabsList>

          <TabsContent value="import" className="space-y-4 overflow-y-auto max-h-[calc(85vh-200px)] px-1 pb-4">
            <Form {...importForm}>
              <form onSubmit={importForm.handleSubmit(onImportSubmit)} className="space-y-4">
                <FormField
                  control={importForm.control}
                  name="elevenLabsAgentId"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Voice Agent ID</FormLabel>
                      <FormControl>
                        <div className="flex space-x-2">
                          <Input
                            {...field}
                            placeholder="Enter Voice Agent ID"
                            disabled={isValidating || importAgentMutation.isPending}
                            data-testid="input-agent-id"
                          />
                          <Button
                            type="button"
                            variant="outline"
                            onClick={onValidate}
                            disabled={isValidating || !field.value}
                            data-testid="button-validate-agent"
                          >
                            {isValidating ? "Validating..." : "Validate"}
                          </Button>
                        </div>
                      </FormControl>
                      <FormDescription>
                        You can find this in your voice service dashboard
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {validatedData && (
                  <div className="p-3 bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 rounded-lg">
                    <p className="text-sm font-medium text-green-800 dark:text-green-200" data-testid="text-validation-success">
                       Agent validated successfully
                    </p>
                    <p className="text-sm text-green-600 dark:text-green-300" data-testid="text-validated-agent-name">
                      Name: {validatedData.name}
                    </p>
                    {validatedData.description && (
                      <p className="text-sm text-green-600 dark:text-green-300" data-testid="text-validated-agent-description">
                        Description: {validatedData.description}
                      </p>
                    )}
                  </div>
                )}

                <FormField
                  control={importForm.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Display Name (Optional)</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          placeholder="Enter a display name"
                          disabled={isValidating || importAgentMutation.isPending}
                          data-testid="input-display-name"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex space-x-3 pt-4">
                  <Button
                    type="submit"
                    className="flex-1"
                    disabled={isValidating || importAgentMutation.isPending || !validatedData}
                    data-testid="button-import-agent"
                  >
                    {importAgentMutation.isPending ? "Importing..." : "Import Agent"}
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    className="flex-1"
                    onClick={handleClose}
                    disabled={isValidating || importAgentMutation.isPending}
                    data-testid="button-cancel"
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            </Form>
          </TabsContent>

          <TabsContent value="create" className="space-y-4 overflow-y-auto max-h-[calc(85vh-200px)] px-1 pb-4">
            <Form {...createForm}>
              <form onSubmit={createForm.handleSubmit(onCreateSubmit)} className="space-y-4">
                <FormField
                  control={createForm.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Agent Name</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          placeholder="Enter agent name"
                          disabled={createAgentMutation.isPending}
                          data-testid="input-create-name"
                        />
                      </FormControl>
                      <FormDescription>
                        Give your agent a descriptive name
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={createForm.control}
                  name="firstMessage"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>First Message</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          placeholder="Hello! How can I assist you today?"
                          disabled={createAgentMutation.isPending}
                          data-testid="input-first-message"
                        />
                      </FormControl>
                      <FormDescription>
                        The greeting message your agent will use
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={createForm.control}
                  name="systemPrompt"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center gap-2">
                        System Prompt
                        <Sparkles className="w-4 h-4 text-purple-500" />
                      </FormLabel>

                      {/* AI Prompt Generator */}
                      <div className="bg-gradient-to-r from-purple-50 to-blue-50 dark:from-purple-900/20 dark:to-blue-900/20 border border-purple-200 dark:border-purple-800 rounded-lg p-3 mb-3">
                        <div className="flex items-center gap-2 mb-2">
                          <Wand2 className="w-4 h-4 text-purple-600" />
                          <span className="text-sm font-medium text-purple-800 dark:text-purple-200">
                            AI Prompt Generator
                          </span>
                        </div>
                        <p className="text-xs text-purple-700 dark:text-purple-300 mb-2">
                          Describe your desired agent and we'll generate a comprehensive system prompt for you
                        </p>
                        <div className="flex gap-2">
                          <Input
                            placeholder="e.g., a customer support agent for your business"
                            value={promptDescription}
                            onChange={(e) => setPromptDescription(e.target.value)}
                            disabled={isGeneratingPrompt || createAgentMutation.isPending}
                            className="flex-1 text-xs"
                            data-testid="input-prompt-description"
                          />
                          <Button
                            type="button"
                            size="sm"
                            onClick={handleGeneratePrompt}
                            disabled={isGeneratingPrompt || !promptDescription.trim() || createAgentMutation.isPending}
                            className="bg-purple-600 hover:bg-purple-700 text-white px-3"
                            data-testid="button-generate-prompt"
                          >
                            {isGeneratingPrompt ? (
                              <>
                                <div className="animate-spin rounded-full h-3 w-3 border-b-2 border-white mr-1"></div>
                                <span className="text-xs">Generating...</span>
                              </>
                            ) : (
                              <>
                                <Sparkles className="w-3 h-3 mr-1" />
                                <span className="text-xs">Generate</span>
                              </>
                            )}
                          </Button>
                        </div>
                      </div>

                      <FormControl>
                        <Textarea
                          {...field}
                          placeholder="You are a helpful AI assistant..."
                          disabled={createAgentMutation.isPending || isGeneratingPrompt}
                          data-testid="input-system-prompt"
                          rows={6}
                        />
                      </FormControl>
                      <FormDescription>
                        Define your agent's personality and behavior. Use the AI generator above for assistance.
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={createForm.control}
                  name="language"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Language</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select a language" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="en">English</SelectItem>
                          <SelectItem value="es">Spanish</SelectItem>
                          <SelectItem value="fr">French</SelectItem>
                          <SelectItem value="de">German</SelectItem>
                          <SelectItem value="it">Italian</SelectItem>
                          <SelectItem value="pt">Portuguese</SelectItem>
                          <SelectItem value="nl">Dutch</SelectItem>
                          <SelectItem value="pl">Polish</SelectItem>
                          <SelectItem value="ja">Japanese</SelectItem>
                          <SelectItem value="ko">Korean</SelectItem>
                          <SelectItem value="zh">Chinese</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormDescription>
                        Primary language for the agent
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="flex space-x-3 pt-4">
                  <Button
                    type="submit"
                    className="flex-1"
                    disabled={createAgentMutation.isPending}
                    data-testid="button-create-agent"
                  >
                    {createAgentMutation.isPending ? "Creating..." : "Create Agent"}
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    className="flex-1"
                    onClick={handleClose}
                    disabled={createAgentMutation.isPending}
                    data-testid="button-cancel-create"
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            </Form>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

client/src/pages/landing.tsx.content:
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useTheme } from "@/components/theme-provider";
import { Moon, Sun, Shield, TrendingUp, Users, Mic, LogIn, Mail, Lock } from "lucide-react";
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useMutation, useQuery } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAgencyPath } from "@/hooks/useAgencyPath";

export default function Landing() {
  const { theme, setTheme } = useTheme();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const { agencySubdomain, buildPath } = useAgencyPath();
  const [subdomain, setSubdomain] = useState<string | null>(null);

  // Detect subdomain from URL path, query parameter, or hostname
  useEffect(() => {
    // First priority: agency path
    if (agencySubdomain) {
      setSubdomain(agencySubdomain);
    } else {
      // Check for subdomain in query parameter (development mode)
      const urlParams = new URLSearchParams(window.location.search);
      const querySubdomain = urlParams.get('subdomain');

      if (querySubdomain) {
        setSubdomain(querySubdomain);
      } else {
        // Check hostname for subdomain
        const hostname = window.location.hostname;
        const parts = hostname.split('.');

        // Check if we have a subdomain (not www, not localhost)
        if (parts.length >= 2 && parts[0] !== 'www' && parts[0] !== 'localhost') {
          setSubdomain(parts[0]);
        }
      }
    }
  }, [agencySubdomain]);

  // Fetch public whitelabel configuration based on subdomain
  const { data: whitelabelConfig } = useQuery<{
    appName?: string;
    companyName?: string;
    logoUrl?: string;
    faviconUrl?: string;
    removePlatformBranding?: boolean;
  }>({
    queryKey: subdomain 
      ? [`/api/whitelabel/subdomain/${subdomain}`]
      : ["/api/whitelabel/public"],
    retry: false,
    enabled: true,
  });


  // Apply whitelabel settings to document
  useEffect(() => {
    if (whitelabelConfig) {
      // Update document title
      if (whitelabelConfig.appName) {
        document.title = whitelabelConfig.appName;
      }

      // Update favicon
      if (whitelabelConfig.faviconUrl) {
        const favicon = document.querySelector("link[rel='icon']") as HTMLLinkElement;
        if (favicon) {
          favicon.href = whitelabelConfig.faviconUrl;
        } else {
          const newFavicon = document.createElement('link');
          newFavicon.rel = 'icon';
          newFavicon.href = whitelabelConfig.faviconUrl;
          document.head.appendChild(newFavicon);
        }
      }
    }
  }, [whitelabelConfig]);

  const loginMutation = useMutation({
    mutationFn: async (credentials: { email: string; password: string }) => {
      const res = await apiRequest("POST", "/api/login", credentials);
      if (!res.ok) {
        throw new Error("Login failed");
      }
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      // Use buildPath to maintain agency context after login
      setLocation(buildPath("/"));
    },
    onError: (error: Error) => {
      toast({
        title: "Login failed",
        description: "Invalid email or password",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    loginMutation.mutate({ email, password });
  };

  return (
    <main className="min-h-screen bg-background flex flex-col" role="main">
      {/* Navigation */}
      <header className="bg-card/80 backdrop-blur-sm border-b border-border" role="banner">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-3">
              {whitelabelConfig?.logoUrl ? (
                <img 
                  src={whitelabelConfig.logoUrl} 
                  alt="Logo" 
                  className="w-8 h-8 object-contain rounded" 
                />
              ) : (
                <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
                  <Mic className="w-4 h-4 text-primary-foreground" />
                </div>
              )}
              <span className="text-xl font-bold text-card-foreground">
                {whitelabelConfig?.appName || "EchoSensei"}
              </span>
            </div>
            <div className="flex items-center">
              <Button 
                variant="ghost" 
                size="sm"
                onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
                data-testid="button-theme-toggle"
              >
                {theme === "dark" ? <Sun className="w-4 h-4" /> : <Moon className="w-4 h-4" />}
              </Button>
            </div>
          </div>
        </div>
      </header>

      {/* Login Section */}
      <section className="flex-1 overflow-y-auto p-4" aria-labelledby="login-heading">
        <div className="w-full max-w-md mx-auto mt-8">
          <Card className="p-6 sm:p-8 bg-card/95 backdrop-blur-sm border-border shadow-2xl">
            <div className="text-center mb-6">
              {whitelabelConfig?.logoUrl ? (
                <img 
                  src={whitelabelConfig.logoUrl} 
                  alt="Logo" 
                  className="w-14 h-14 object-contain rounded-2xl mx-auto mb-3" 
                />
              ) : (
                <div className="w-14 h-14 bg-primary rounded-2xl flex items-center justify-center mx-auto mb-3">
                  <Mic className="w-7 h-7 text-primary-foreground" />
                </div>
              )}
              <h1 id="login-heading" className="text-2xl font-bold text-card-foreground mb-1">Welcome Back</h1>
              <p className="text-sm text-muted-foreground">
                {whitelabelConfig?.companyName 
                  ? `Sign in to access ${whitelabelConfig.companyName}` 
                  : "Sign in to access your EchoSensei"}
              </p>
            </div>

            <form 
              className="space-y-4"
              onSubmit={handleSubmit}
              aria-label="Login form"
            >
              <div className="space-y-2">
                <Label htmlFor="email" className="text-sm font-medium">
                  Email
                </Label>
                <div className="relative">
                  <Mail className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                  <Input
                    id="email"
                    type="email"
                    placeholder="Enter your email"
                    className="pl-10 h-11"
                    data-testid="input-email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="password" className="text-sm font-medium">
                  Password
                </Label>
                <div className="relative">
                  <Lock className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                  <Input
                    id="password"
                    type="password"
                    placeholder="Enter your password"
                    className="pl-10 h-11"
                    data-testid="input-password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
              </div>

              <Button 
                type="submit"
                className="w-full h-11 mt-6 bg-primary text-primary-foreground hover:bg-primary/90" 
                size="lg" 
                data-testid="button-login"
                disabled={loginMutation.isPending}
              >
                {loginMutation.isPending ? (
                  "Signing in..."
                ) : (
                  <>
                    <LogIn className="w-5 h-5 mr-2" />
                    Sign In
                  </>
                )}
              </Button>
            </form>
          </Card>

          {/* Info Section */}
          <div className="mt-6 text-center">
            {!whitelabelConfig?.removePlatformBranding && (
              <>
                <h3 className="text-lg font-semibold text-card-foreground mb-4">
                  What is {whitelabelConfig?.appName || "EchoSensei"}?
                </h3>
                <p className="text-sm text-muted-foreground mb-6 max-w-md mx-auto" data-testid="text-info-description">
                  A comprehensive monitoring platform for voice AI agents with enterprise-grade security, 
                  real-time analytics, and multi-tenant support.
                </p>
              </>
            )}

            {/* Feature Cards */}
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mt-8">
              <div className="text-center">
                <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center mx-auto mb-2">
                  <Shield className="w-5 h-5 text-primary" />
                </div>
                <p className="text-xs text-muted-foreground">Secure BYOK</p>
              </div>
              <div className="text-center">
                <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center mx-auto mb-2">
                  <TrendingUp className="w-5 h-5 text-primary" />
                </div>
                <p className="text-xs text-muted-foreground">Real-time Analytics</p>
              </div>
              <div className="text-center">
                <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center mx-auto mb-2">
                  <Users className="w-5 h-5 text-primary" />
                </div>
                <p className="text-xs text-muted-foreground">Multi-tenant</p>
              </div>
            </div>
          </div>
        </div>
      </section>
    </main>
  );
}

client/src/components/ui/toggle.tsx.content:
import * as React from "react"
import * as TogglePrimitive from "@radix-ui/react-toggle"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const toggleVariants = cva(
  "inline-flex items-center justify-center rounded-md text-sm font-medium ring-offset-background transition-colors hover:bg-muted hover:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 data-[state=on]:bg-accent data-[state=on]:text-accent-foreground [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0 gap-2",
  {
    variants: {
      variant: {
        default: "bg-transparent",
        outline:
          "border border-input bg-transparent hover:bg-accent hover:text-accent-foreground",
      },
      size: {
        default: "h-10 px-3 min-w-10",
        sm: "h-9 px-2.5 min-w-9",
        lg: "h-11 px-5 min-w-11",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
)

const Toggle = React.forwardRef<
  React.ElementRef<typeof TogglePrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof TogglePrimitive.Root> &
    VariantProps<typeof toggleVariants>
>(({ className, variant, size, ...props }, ref) => (
  <TogglePrimitive.Root
    ref={ref}
    className={cn(toggleVariants({ variant, size, className }))}
    {...props}
  />
))

Toggle.displayName = TogglePrimitive.Root.displayName

export { Toggle, toggleVariants }

