  --shadow-md: 0px 6px 12px -2px rgba(0, 0, 0, 0.4), 0px 12px 20px -4px rgba(0, 0, 0, 0.5);
  --shadow-lg: 0px 10px 20px -5px rgba(0, 0, 0, 0.4), 0px 20px 25px -5px rgba(0, 0, 0, 0.5);
