@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    background-image: 
      radial-gradient(at 0% 0%, hsla(263, 90%, 51%, 0.03) 0px, transparent 50%),
      radial-gradient(at 100% 0%, hsla(217, 91%, 60%, 0.03) 0px, transparent 50%),
      radial-gradient(at 100% 100%, hsla(263, 90%, 51%, 0.03) 0px, transparent 50%),
      radial-gradient(at 0% 100%, hsla(217, 91%, 60%, 0.03) 0px, transparent 50%);
  }

  .dark body {
    background-image: 
      radial-gradient(at 0% 0%, hsla(263, 90%, 51%, 0.08) 0px, transparent 50%),
      radial-gradient(at 100% 0%, hsla(217, 91%, 60%, 0.08) 0px, transparent 50%),
      radial-gradient(at 100% 100%, hsla(263, 90%, 51%, 0.08) 0px, transparent 50%),
      radial-gradient(at 0% 100%, hsla(217, 91%, 60%, 0.08) 0px, transparent 50%);
  }
}

/* Optimized animations */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

/* Performance optimizations */
* {
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Mobile-first responsive utilities */
@media (max-width: 640px) {
  .hide-mobile { display: none !important; }
  .mobile-full { width: 100% !important; }
  .mobile-text-xs { font-size: 0.75rem !important; }
  .mobile-text-sm { font-size: 0.875rem !important; }
  .mobile-p-2 { padding: 0.5rem !important; }
  .mobile-p-3 { padding: 0.75rem !important; }
  .mobile-gap-2 { gap: 0.5rem !important; }
  .mobile-gap-3 { gap: 0.75rem !important; }
}

@media (min-width: 641px) {
  .hide-desktop { display: none !important; }
}

/* Tablet responsive utilities */
@media (max-width: 768px) {
  .tablet-stack { flex-direction: column !important; }
  .tablet-w-full { width: 100% !important; }
  .tablet-gap-3 { gap: 0.75rem !important; }
}

/* Mobile viewport optimization */
@viewport {
  width: device-width;
  initial-scale: 1.0;
}

/* Container and spacing utilities */
.container-mobile {
  @apply px-4 sm:px-6 lg:px-8;
}

/* Touch-friendly sizing */
.touch-target {
  min-height: 44px;
  min-width: 44px;
}

/* Mobile-friendly scrolling */
.scroll-smooth-mobile {
  -webkit-overflow-scrolling: touch;
  scroll-behavior: smooth;
}

/* Card responsive styling */
.card-mobile {
  @apply rounded-lg p-4 sm:p-6 lg:p-8;
}

/* Extra small screen breakpoint for grid columns */
@media (min-width: 475px) {
  .xs\:grid-cols-2 { grid-template-columns: repeat(2, minmax(0, 1fr)); }
}

/* Smooth scrolling */
html { scroll-behavior: smooth; }

/* Focus visible for accessibility */
:focus-visible {
  outline: 2px solid var(--primary);
  outline-offset: 2px;
}

/* Premium gradient backgrounds */
.gradient-purple {
  background: linear-gradient(135deg, #8b5cf6 0%, #7c3aed 50%, #6d28d9 100%);
  box-shadow: 0 10px 40px -10px rgba(139, 92, 246, 0.4);
}

.gradient-blue {
  background: linear-gradient(135deg, #667eea 0%, #5b6ff5 50%, #4f46e5 100%);
  box-shadow: 0 10px 40px -10px rgba(102, 126, 234, 0.4);
}

.gradient-dark {
  background: linear-gradient(135deg, #1e1b4b 0%, #2d2463 50%, #312e81 100%);
}

.gradient-emerald {
  background: linear-gradient(135deg, #10b981 0%, #059669 50%, #047857 100%);
  box-shadow: 0 10px 40px -10px rgba(16, 185, 129, 0.4);
}

.gradient-amber {
  background: linear-gradient(135deg, #f59e0b 0%, #d97706 50%, #b45309 100%);
  box-shadow: 0 10px 40px -10px rgba(245, 158, 11, 0.4);
}

.gradient-rose {
  background: linear-gradient(135deg, #f43f5e 0%, #e11d48 50%, #be123c 100%);
  box-shadow: 0 10px 40px -10px rgba(244, 63, 94, 0.4);
}

.gradient-cyan {
  background: linear-gradient(135deg, #06b6d4 0%, #0891b2 50%, #0e7490 100%);
  box-shadow: 0 10px 40px -10px rgba(6, 182, 212, 0.4);
}

/* Improved card hover effects */
.card-hover {
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
}

.card-hover::before {
  content: '';
  position: absolute;
  inset: 0;
  border-radius: inherit;
  padding: 1px;
  background: linear-gradient(135deg, rgba(139, 92, 246, 0.3), rgba(102, 126, 234, 0.3));
  -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
  -webkit-mask-composite: xor;
  mask-composite: exclude;
  opacity: 0;
  transition: opacity 0.4s ease;
}

.card-hover:hover {
  transform: translateY(-6px) scale(1.01);
  box-shadow: 0 20px 40px -10px rgba(139, 92, 246, 0.3), 0 10px 20px -5px rgba(0, 0, 0, 0.1);
}

.card-hover:hover::before {
  opacity: 1;
}

.dark .card-hover:hover {
  box-shadow: 0 20px 40px -10px rgba(139, 92, 246, 0.5), 0 10px 20px -5px rgba(0, 0, 0, 0.3);
}

/* Premium button styles */
.btn-premium {
  position: relative;
  overflow: hidden;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  background: linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%);
}

.btn-premium::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
  transition: left 0.6s ease;
}

.btn-premium:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 30px -5px rgba(139, 92, 246, 0.5);
}

.btn-premium:hover::before {
  left: 100%;
}

.btn-premium:active {
  transform: translateY(0);
}

/* Glow effects */
.glow-purple {
  box-shadow: 0 0 30px rgba(139, 92, 246, 0.4), 0 0 60px rgba(139, 92, 246, 0.2);
  animation: glow-pulse 3s ease-in-out infinite;
}

.glow-purple-hover {
  transition: all 0.3s ease;
}

.glow-purple-hover:hover {
  box-shadow: 0 0 40px rgba(139, 92, 246, 0.6), 0 0 80px rgba(139, 92, 246, 0.3);
}

@keyframes glow-pulse {
  0%, 100% {
    box-shadow: 0 0 30px rgba(139, 92, 246, 0.4), 0 0 60px rgba(139, 92, 246, 0.2);
  }
  50% {
    box-shadow: 0 0 40px rgba(139, 92, 246, 0.6), 0 0 80px rgba(139, 92, 246, 0.3);
  }
}

/* Animated gradient text */
.gradient-text {
  background: linear-gradient(135deg, #8b5cf6 0%, #7c3aed 50%, #6d28d9 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  background-size: 200% auto;
  animation: gradient-shift 3s ease infinite;
}

@keyframes gradient-shift {
  0%, 100% {
    background-position: 0% center;
  }
  50% {
    background-position: 100% center;
  }
}

/* Pulse animation */
@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

.pulse {
  animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

/* Float animation */
@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
