}

/* Subtle hover lift for nav items */
.hover-lift-subtle {
  transition: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

.hover-lift-subtle:hover {
  transform: translateX(4px);
}

.hover-lift-subtle:active {
  transform: translateX(2px);
