}

.brand-gradient-text {
  background: linear-gradient(
    135deg,
    color-mix(in hsl, var(--primary) 90%, white 10%) 0%,
    var(--primary) 50%,
    color-mix(in hsl, var(--primary) 90%, black 10%) 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
