import { promises as fs } from 'fs';
import path from 'path';

export interface AudioMetadata {
  conversationId: string;
  callId?: string;
  organizationId?: string;
  agentId?: string;
  uploadedAt: string;
  fileSize: number;
  duration?: number;
  format?: string;
}

class AudioStorageService {
  private storageDir: string;

  constructor(storageDir: string = 'audio-storage') {
    this.storageDir = storageDir;
    this.ensureStorageDirectory();
  }

  private async ensureStorageDirectory(): Promise<void> {
    try {
      await fs.access(this.storageDir);
    } catch {
      await fs.mkdir(this.storageDir, { recursive: true });
      console.log(`Created audio storage directory: ${this.storageDir}`);
    }
  }

  private sanitizeFileName(filename: string): string {
    // Remove any path traversal attempts and keep only safe characters
    return filename.replace(/[^a-zA-Z0-9_\-\.]/g, '_');
  }

  private generateStorageKey(conversationId: string): string {
    const timestamp = Date.now();
    const sanitizedId = this.sanitizeFileName(conversationId);
    return `${sanitizedId}_${timestamp}.mp3`;
  }

  private getFilePath(storageKey: string): string {
    const sanitizedKey = this.sanitizeFileName(storageKey);
    return path.join(this.storageDir, sanitizedKey);
  }

  /**
   * Get the full file path for a given filename (public method for serving files)
   * Returns null if the filename is invalid or contains path traversal attempts
   */
  public getFilePathForServing(fileName: string): string | null {
    // Strict validation: only allow alphanumeric, underscores, hyphens, and .mp3 extension
    if (!/^[a-zA-Z0-9_-]+\.mp3$/.test(fileName)) {
      console.warn(`Invalid audio filename format: ${fileName}`);
      return null;
    }

    const filePath = path.join(this.storageDir, fileName);
    const absoluteFilePath = path.resolve(filePath);
    const absoluteStorageDir = path.resolve(this.storageDir);

    // Ensure the file is within the storage directory (prevent path traversal)
    if (!absoluteFilePath.startsWith(absoluteStorageDir)) {
      console.warn(`Path traversal attempt detected: ${fileName}`);
      return null;
    }

    return filePath;
  }

  private getMetadataPath(storageKey: string): string {
    return `${this.getFilePath(storageKey)}.meta.json`;
  }

  /**
   * Upload audio file to storage
   */
  async uploadAudio(
    conversationId: string,
    audioBuffer: Buffer,
    metadata: Partial<AudioMetadata> = {}
  ): Promise<{ storageKey: string; filePath: string }> {
    await this.ensureStorageDirectory();

    const storageKey = this.generateStorageKey(conversationId);
    const filePath = this.getFilePath(storageKey);

    // Write audio file
    await fs.writeFile(filePath, audioBuffer);
    console.log(`Audio uploaded: ${filePath} (${audioBuffer.length} bytes)`);

    // Write metadata
    const fullMetadata: AudioMetadata = {
      conversationId,
      uploadedAt: new Date().toISOString(),
      fileSize: audioBuffer.length,
      ...metadata,
    };

    await fs.writeFile(
      this.getMetadataPath(storageKey),
      JSON.stringify(fullMetadata, null, 2)
    );

    return { storageKey, filePath };
  }

  /**
   * Get public URL for audio file
   */
  getSignedUrl(storageKey: string): string {
    const sanitizedKey = this.sanitizeFileName(storageKey);
    return `/api/audio/${sanitizedKey}`;
  }

  /**
   * Check if audio file exists
   */
  async audioExists(storageKey: string): Promise<boolean> {
    try {
      const filePath = this.getFilePath(storageKey);
      await fs.access(filePath);
      return true;
    } catch {
      return false;
    }
  }

  /**
   * Download audio file
   */
  async downloadAudio(storageKey: string): Promise<Buffer> {
    const filePath = this.getFilePath(storageKey);
    return await fs.readFile(filePath);
  }

  /**
   * Delete audio file and its metadata
   */
  async deleteAudio(storageKey: string): Promise<void> {
    const filePath = this.getFilePath(storageKey);
    const metadataPath = this.getMetadataPath(storageKey);

    try {
      await fs.unlink(filePath);
      console.log(`Deleted audio file: ${filePath}`);
    } catch (error) {
      console.error(`Failed to delete audio file: ${filePath}`, error);
    }

    try {
      await fs.unlink(metadataPath);
      console.log(`Deleted metadata file: ${metadataPath}`);
    } catch (error) {
      // Metadata file might not exist, which is fine
    }
  }

  /**
   * Get audio metadata
   */
  async getAudioMetadata(storageKey: string): Promise<AudioMetadata | null> {
    try {
      const metadataPath = this.getMetadataPath(storageKey);
      const data = await fs.readFile(metadataPath, 'utf-8');
      return JSON.parse(data);
    } catch {
      return null;
    }
  }

  /**
   * List all audio files in storage
   */
  async listAudioFiles(): Promise<string[]> {
    try {
      const files = await fs.readdir(this.storageDir);
      return files.filter(file => file.endsWith('.mp3'));
    } catch {
      return [];
    }
  }
}

export default AudioStorageService;

trigger-batch-summary.js.content:
// Script to trigger batch summary generation for all calls
const fetch = require('node-fetch');

async function triggerBatchSummaryGeneration() {
  try {
    console.log('[TRIGGER] Starting batch summary generation...');

    // First, login to get authenticated session
    const loginResponse = await fetch('http://localhost:5000/api/auth/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        email: 'cc@siwaht.com',
        password: 'password123'
      })
    });

    if (!loginResponse.ok) {
      throw new Error(`Login failed: ${loginResponse.status}`);
    }

    const loginData = await loginResponse.json();
    console.log('[TRIGGER] Logged in successfully');

    // Extract cookies from login response
    const cookies = loginResponse.headers.get('set-cookie');

    // Now trigger the batch summary generation
    const summaryResponse = await fetch('http://localhost:5000/api/jobs/generate-all-summaries', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Cookie': cookies || ''
      }
    });

    if (!summaryResponse.ok) {
      const errorText = await summaryResponse.text();
      throw new Error(`Summary generation failed: ${summaryResponse.status} - ${errorText}`);
    }

    const result = await summaryResponse.json();
    console.log('[TRIGGER] Batch summary generation completed:');
    console.log(`- Processed: ${result.processed || 0} calls`);
    console.log(`- Successful: ${result.successful || 0} summaries`);
    console.log(`- Failed: ${result.failed || 0} summaries`);

    if (result.errors && result.errors.length > 0) {
      console.log('[TRIGGER] Errors encountered:');
      result.errors.forEach(error => console.log(`  - ${error}`));
    }

    console.log('[TRIGGER] Batch summary generation task completed!');

  } catch (error) {
    console.error('[TRIGGER] Error:', error.message);
    process.exit(1);
  }
}

// Run the trigger
triggerBatchSummaryGeneration();

server/test-batch-summary.ts.content:
// Test script to trigger batch summary generation directly
import { storage } from './storage';
import SummaryService from './services/summary-service';

async function triggerBatchSummaryGeneration() {
  try {
    // Get admin user for testing
    const adminUser = await storage.getUserByEmail('cc@siwaht.com');
    if (!adminUser) {
      console.error('Admin user not found');
      return;
    }

    console.log('[BATCH-SUMMARY] Starting batch summary generation for organization:', adminUser.organizationId);

    // Get all call logs for the organization
    const allCallLogs = await storage.getCallLogs(adminUser.organizationId);

    // Extract data from paginated response
    const callLogsData = allCallLogs.data || allCallLogs;

    // Filter to only those needing summaries
    const callLogsNeedingSummary = callLogsData.filter((log: any) => 
      log.transcript && (!log.summary || log.summaryStatus === 'failed')
    );

    console.log(`[BATCH-SUMMARY] Found ${callLogsNeedingSummary.length} calls needing summaries`);

    if (callLogsNeedingSummary.length === 0) {
      console.log('No calls need summary generation');
      return;
    }

    let successful = 0;
    let failed = 0;
    const errors: string[] = [];

    // Process in batches to avoid overwhelming the system
    const batchSize = 5;
    for (let i = 0; i < callLogsNeedingSummary.length; i += batchSize) {
      const batch = callLogsNeedingSummary.slice(i, i + batchSize);

      await Promise.all(batch.map(async (callLog: any) => {
        try {
          console.log(`[BATCH-SUMMARY] Generating summary for call: ${callLog.id}`);

          const result = await SummaryService.generateCallSummary(callLog);

          if (result.status === 'success' && result.summary) {
            await storage.updateCallLogSummary(
              callLog.id,
              adminUser.organizationId,
              result.summary,
              result.status,
              result.metadata
            );
            successful++;
            console.log(`[BATCH-SUMMARY] Successfully generated summary for call: ${callLog.id}`);
          } else {
            failed++;
            errors.push(`Call ${callLog.id}: ${result.error || 'Unknown error'}`);
            console.error(`[BATCH-SUMMARY] Failed to generate summary for call: ${callLog.id}`, result.error);
          }
        } catch (error: any) {
          failed++;
          errors.push(`Call ${callLog.id}: ${error.message}`);
          console.error(`[BATCH-SUMMARY] Error processing call ${callLog.id}:`, error);
        }
      }));

      console.log(`[BATCH-SUMMARY] Processed batch ${Math.floor(i / batchSize) + 1}/${Math.ceil(callLogsNeedingSummary.length / batchSize)}`);
    }

    console.log('\n================================');
    console.log('[BATCH-SUMMARY]  Batch summary generation completed:');
    console.log(` Total Processed: ${callLogsNeedingSummary.length} calls`);
    console.log(` Successful: ${successful} summaries`);
    console.log(` Failed: ${failed} summaries`);

    if (errors.length > 0) {
      console.log('\n Errors encountered:');
      errors.forEach(error => console.log(`  - ${error}`));
    }

    console.log('\n Batch summary generation completed successfully!');

    // Exit successfully
    process.exit(0);
  } catch (error: any) {
    console.error('[BATCH-SUMMARY] Fatal error:', error.message);
    process.exit(1);
  }
}

// Run the batch summary generation
triggerBatchSummaryGeneration();

server/services/data-cleanup-service.ts.content:
import { IStorage } from "../storage";
import AudioStorageService from "./audio-storage-service";

export class DataCleanupService {
  constructor(
    private storage: IStorage,
    private audioStorage: AudioStorageService
  ) {}

  /**
   * Completely wipes all data for an organization when switching ElevenLabs accounts
   * This includes: call logs, agents, recordings, and analytics
   */
  async wipeOrganizationData(organizationId: string): Promise<{
    success: boolean;
    deleted: {
      callLogs: number;
      agents: number;
      recordings: number;
    };
    error?: string;
  }> {
    try {
      console.log(`[DATA-CLEANUP] Starting data wipe for organization ${organizationId}`);

      // Step 1: Get all call logs to delete their recordings
      const callLogsResult = await this.storage.getCallLogs(organizationId);
      const callLogs = callLogsResult.data;
      console.log(`[DATA-CLEANUP] Found ${callLogs.length} call logs to delete`);

      // Step 2: Delete all audio recordings from storage
      let recordingsDeleted = 0;
      for (const call of callLogs) {
        if (call.audioStorageKey) {
          try {
            await this.audioStorage.deleteAudio(call.audioStorageKey);
            recordingsDeleted++;
          } catch (error) {
            console.error(`[DATA-CLEANUP] Failed to delete recording ${call.audioStorageKey}:`, error);
            // Continue even if one recording fails to delete
          }
        }
      }
      console.log(`[DATA-CLEANUP] Deleted ${recordingsDeleted} recordings from storage`);

      // Step 3: Delete all call logs from database
      const callLogsDeleted = await this.storage.deleteAllCallLogs(organizationId);
      console.log(`[DATA-CLEANUP] Deleted ${callLogsDeleted} call logs from database`);

      // Step 4: Delete all agents
      const agentsDeleted = await this.storage.deleteAllAgents(organizationId);
      console.log(`[DATA-CLEANUP] Deleted ${agentsDeleted} agents from database`);

      console.log(`[DATA-CLEANUP]  Data wipe complete for organization ${organizationId}`);

      return {
        success: true,
        deleted: {
          callLogs: callLogsDeleted,
          agents: agentsDeleted,
          recordings: recordingsDeleted,
        },
      };
    } catch (error: any) {
      console.error(`[DATA-CLEANUP]  Error wiping organization data:`, error);
      return {
        success: false,
        deleted: {
          callLogs: 0,
          agents: 0,
          recordings: 0,
        },
        error: error.message,
      };
    }
  }
}

export default DataCleanupService;

server/middleware/api-key-change-detecto.content:
import { Request, Response, NextFunction } from "express";
import { createHash } from "crypto";
import { IStorage } from "../storage";
import DataCleanupService from "../services/data-cleanup-service";
import AudioStorageService from "../services/audio-storage-service";
import { db } from "../db";
import { organizations } from "@shared/schema";
import { eq } from "drizzle-orm";

/**
 * Hashes an API key for comparison
 */
function hashApiKey(apiKey: string): string {
  return createHash('sha256').update(apiKey).digest('hex');
}

/**
 * Middleware that detects ElevenLabs API key changes and automatically wipes old data
 * This ensures when a user switches to a different ElevenLabs account, all old data is cleared
 */
export async function detectApiKeyChange(
  req: Request,
  res: Response,
  next: NextFunction
) {
  try {
    // Only check for authenticated users with an organization
    if (!req.user || !req.user.organizationId) {
      return next();
    }

    const elevenLabsApiKey = process.env.ELEVENLABS_API_KEY;
    if (!elevenLabsApiKey) {
      // No API key configured, skip check
      return next();
    }

    // Get the organization
    const [org] = await db()
      .select()
      .from(organizations)
      .where(eq(organizations.id, req.user.organizationId));

    if (!org) {
      return next();
    }

    const currentKeyHash = hashApiKey(elevenLabsApiKey);
    const storedKeyHash = org.elevenLabsApiKeyHash;

    // If this is the first time or the key has changed
    if (!storedKeyHash || storedKeyHash !== currentKeyHash) {
      console.log(`[API-KEY-CHANGE] Detected API key change for organization ${org.id}`);

      // Only wipe data if there was a previous key (not first time setup)
      if (storedKeyHash) {
        console.log(`[API-KEY-CHANGE] Wiping old data for organization ${org.id}`);

        const storage = req.app.locals.storage as IStorage;
        const audioStorage = new AudioStorageService();
        const cleanupService = new DataCleanupService(storage, audioStorage);

        const result = await cleanupService.wipeOrganizationData(org.id);

        if (!result.success) {
          console.error(`[API-KEY-CHANGE]  Data wipe failed, will retry on next request:`, result.error);
          // Don't update hash or trigger sync - let it retry on next request
          return next();
        }

        console.log(`[API-KEY-CHANGE]  Successfully wiped data:`, result.deleted);
      }

      // Update the stored API key hash (only after successful wipe or first-time setup)
      await db()
        .update(organizations)
        .set({ elevenLabsApiKeyHash: currentKeyHash })
        .where(eq(organizations.id, org.id));

      console.log(`[API-KEY-CHANGE] Updated API key hash for organization ${org.id}`);

      // Also update or create the ElevenLabs integration with the new API key
      const storage = req.app.locals.storage as IStorage;
      console.log(`[API-KEY-CHANGE] Upserting ElevenLabs integration with new API key`);

      await storage.upsertIntegration({
        organizationId: org.id,
        provider: "elevenlabs",
        apiKey: elevenLabsApiKey,
        status: "ACTIVE",
      });

      console.log(`[API-KEY-CHANGE] ElevenLabs integration updated successfully`);

      // Trigger auto-sync in the background (don't block the request)
      if (storedKeyHash) {
        // Import sync service dynamically to avoid circular dependencies
        setImmediate(async () => {
          try {
            const { SyncService } = await import("../services/sync-service");
            console.log(`[API-KEY-CHANGE] Starting auto-sync for organization ${org.id}`);
            // Sync both agents and call logs for the new account
            await SyncService.syncAgents(org.id);
            await SyncService.syncCallLogs({ organizationId: org.id, includeTranscripts: true });
            console.log(`[API-KEY-CHANGE] Auto-sync completed for organization ${org.id}`);
          } catch (error) {
            console.error(`[API-KEY-CHANGE] Auto-sync failed:`, error);
          }
        });
      }
    }

    next();
  } catch (error) {
    console.error("[API-KEY-CHANGE] Error in API key change detection:", error);
    // Don't block the request on error
    next();
  }
}

server/storage/s3-storage-adapter.ts.content:
import type { StorageAdapter, StorageMetadata } from './storage-adapter';

/**
 * AWS S3 Storage Adapter
 * 
 * Stores files in Amazon S3. Suitable for:
 * - Production deployments on AWS
 * - Multi-server/containerized environments
 * - High-availability systems
 * - Serverless architectures (Lambda, ECS, Fargate)
 * 
 * Requires AWS SDK to be installed: npm install @aws-sdk/client-s3 @aws-sdk/s3-request-presigner
 */
export class S3StorageAdapter implements StorageAdapter {
  private s3Client: any;
  private bucket: string;

  constructor(config: {
    bucket: string;
    region: string;
    accessKeyId: string;
    secretAccessKey: string;
  }) {
    this.bucket = config.bucket;

    // Lazy load AWS SDK (only if using S3)
    this.initializeS3Client(config);
  }

  private async initializeS3Client(config: any) {
    try {
      const { S3Client } = await import('@aws-sdk/client-s3');

      this.s3Client = new S3Client({
        region: config.region,
        credentials: {
          accessKeyId: config.accessKeyId,
          secretAccessKey: config.secretAccessKey,
        },
      });

      console.log(`[S3-STORAGE] Initialized S3 client for bucket: ${this.bucket}`);
    } catch (error) {
      throw new Error(
        'AWS SDK not installed. Run: npm install @aws-sdk/client-s3 @aws-sdk/s3-request-presigner'
      );
    }
  }

  async save(key: string, buffer: Buffer, metadata?: StorageMetadata): Promise<string> {
    const { PutObjectCommand } = await import('@aws-sdk/client-s3');

    const command = new PutObjectCommand({
      Bucket: this.bucket,
      Key: key,
      Body: buffer,
      ContentType: metadata?.contentType || 'application/octet-stream',
      CacheControl: metadata?.cacheControl,
      Metadata: metadata?.metadata,
    });

    await this.s3Client.send(command);
    return key;
  }

  async get(key: string): Promise<Buffer> {
    const { GetObjectCommand } = await import('@aws-sdk/client-s3');

    const command = new GetObjectCommand({
      Bucket: this.bucket,
      Key: key,
    });

    const response = await this.s3Client.send(command);
    const chunks: Uint8Array[] = [];

    for await (const chunk of response.Body as any) {
      chunks.push(chunk);
    }

    return Buffer.concat(chunks);
  }

  async delete(key: string): Promise<void> {
    const { DeleteObjectCommand } = await import('@aws-sdk/client-s3');

    const command = new DeleteObjectCommand({
      Bucket: this.bucket,
      Key: key,
    });

    await this.s3Client.send(command);
  }

  async exists(key: string): Promise<boolean> {
    const { HeadObjectCommand } = await import('@aws-sdk/client-s3');

    try {
      const command = new HeadObjectCommand({
        Bucket: this.bucket,
        Key: key,
      });

      await this.s3Client.send(command);
      return true;
    } catch (error: any) {
      if (error.name === 'NotFound') {
        return false;
      }
      throw error;
    }
  }

  getPublicUrl(key: string): string | null {
    // Return the standard S3 URL (works if bucket is public)
    const region = this.s3Client.config.region;
    return `https://${this.bucket}.s3.${region}.amazonaws.com/${key}`;
  }

  async getSignedUrl(key: string, expiresIn: number = 3600): Promise<string | null> {
    const { GetObjectCommand } = await import('@aws-sdk/client-s3');
    const { getSignedUrl } = await import('@aws-sdk/s3-request-presigner');

    const command = new GetObjectCommand({
      Bucket: this.bucket,
      Key: key,
    });

    return await getSignedUrl(this.s3Client, command, { expiresIn });
  }
}

server/storage/storage-factory.ts.content:
import { config } from '../config';
import type { StorageAdapter } from './storage-adapter';
import { LocalStorageAdapter } from './local-storage-adapter';

/**
 * Storage Factory
 * 
 * Creates and returns the appropriate storage adapter based on configuration.
 * Supports lazy loading of cloud SDKs to avoid bundling unnecessary dependencies.
 */
export class StorageFactory {
  private static instance: StorageAdapter | null = null;

  /**
   * Get the configured storage adapter (singleton)
   */
  static getAdapter(): StorageAdapter {
    if (this.instance) {
      return this.instance;
    }

    const { storage } = config;

    switch (storage.provider) {
      case 'local':
        console.log('[STORAGE] Using local filesystem storage');
        this.instance = new LocalStorageAdapter(storage.local.audioDir);
        break;

      case 's3':
        if (!storage.s3) {
          throw new Error('S3 configuration is missing');
        }
        console.log('[STORAGE] Using AWS S3 storage');
        this.instance = this.createS3Adapter(storage.s3);
        break;

      case 'gcs':
        if (!storage.gcs) {
          throw new Error('GCS configuration is missing');
        }
        console.log('[STORAGE] Using Google Cloud Storage');
        this.instance = this.createGCSAdapter(storage.gcs);
        break;

      case 'azure':
        if (!storage.azure) {
          throw new Error('Azure configuration is missing');
        }
        console.log('[STORAGE] Using Azure Blob Storage');
        this.instance = this.createAzureAdapter(storage.azure);
        break;

      default:
        throw new Error(`Unknown storage provider: ${storage.provider}`);
    }

    return this.instance;
  }

  private static createS3Adapter(config: NonNullable<typeof config.storage.s3>): StorageAdapter {
    const { S3StorageAdapter } = require('./s3-storage-adapter');
    return new S3StorageAdapter(config);
  }

  private static createGCSAdapter(config: NonNullable<typeof config.storage.gcs>): StorageAdapter {
    // GCS adapter can be implemented similarly to S3
    // For now, throw an error to indicate it needs implementation
    throw new Error(
      'Google Cloud Storage adapter not yet implemented. ' +
      'To add support, create server/storage/gcs-storage-adapter.ts following the S3 adapter pattern.'
    );
  }

  private static createAzureAdapter(config: NonNullable<typeof config.storage.azure>): StorageAdapter {
    // Azure adapter can be implemented similarly to S3
    // For now, throw an error to indicate it needs implementation
    throw new Error(
      'Azure Blob Storage adapter not yet implemented. ' +
      'To add support, create server/storage/azure-storage-adapter.ts following the S3 adapter pattern.'
    );
  }

  /**
   * Reset the singleton instance (useful for testing)
   */
  static reset(): void {
    this.instance = null;
  }
}

/**
 * Get the configured storage adapter
 */
export function getStorageAdapter(): StorageAdapter {
  return StorageFactory.getAdapter();
}

server/storage/local-storage-adapter.ts.content:
import * as fs from 'fs/promises';
import * as path from 'path';
import type { StorageAdapter, StorageMetadata } from './storage-adapter';

/**
 * Local Filesystem Storage Adapter
 * 
 * Stores files on the local filesystem. Suitable for:
 * - Development environments
 * - Single-server deployments
 * - Testing
 * 
 * Not recommended for:
 * - Multi-server/containerized deployments
 * - Serverless environments
 * - High-availability production systems
 */
export class LocalStorageAdapter implements StorageAdapter {
  constructor(private baseDir: string) {
    // Ensure base directory exists
    this.ensureDirectoryExists(baseDir);
  }

  private async ensureDirectoryExists(dir: string): Promise<void> {
    try {
      await fs.access(dir);
    } catch {
      await fs.mkdir(dir, { recursive: true });
    }
  }

  async save(key: string, buffer: Buffer, metadata?: StorageMetadata): Promise<string> {
    const filePath = path.join(this.baseDir, key);
    const dir = path.dirname(filePath);

    // Ensure directory exists
    await this.ensureDirectoryExists(dir);

    // Save file
    await fs.writeFile(filePath, buffer);

    // Save metadata if provided
    if (metadata) {
      const metaPath = `${filePath}.meta.json`;
      await fs.writeFile(metaPath, JSON.stringify(metadata, null, 2));
    }

    return key;
  }

  async get(key: string): Promise<Buffer> {
    const filePath = path.join(this.baseDir, key);
    return await fs.readFile(filePath);
  }

  async delete(key: string): Promise<void> {
    const filePath = path.join(this.baseDir, key);
    const metaPath = `${filePath}.meta.json`;

    try {
      await fs.unlink(filePath);
    } catch (error: any) {
      if (error.code !== 'ENOENT') throw error;
    }

    try {
      await fs.unlink(metaPath);
    } catch (error: any) {
      if (error.code !== 'ENOENT') throw error;
    }
  }

  async exists(key: string): Promise<boolean> {
    const filePath = path.join(this.baseDir, key);
    try {
      await fs.access(filePath);
      return true;
    } catch {
      return false;
    }
  }

  getPublicUrl(key: string): string | null {
    // Local storage doesn't provide public URLs
    // Files must be served through the application
    return `/api/audio/${key}`;
  }

  async getSignedUrl(key: string, expiresIn?: number): Promise<string | null> {
    // Local storage doesn't support signed URLs
    // Return the same as public URL
    return this.getPublicUrl(key);
  }

  /**
   * Get the absolute file path for a given key
   * Useful for serving files directly with res.sendFile()
   */
  getFilePath(key: string): string {
    return path.join(this.baseDir, key);
  }

  /**
   * Validate that a key is safe (prevents path traversal)
   */
  static isValidKey(key: string): boolean {
    // Prevent path traversal
    const normalizedKey = path.normalize(key);
    return !normalizedKey.includes('..') && !path.isAbsolute(normalizedKey);
  }
}

