                onChange={(e) => setNewUser({ ...newUser, companyName: e.target.value })}
                placeholder={newUser.userType === "agency" ? "Acme Agency" : "Acme Corp (optional)"}
                data-testid="input-new-user-company"
                required={newUser.userType === "agency"}
              />
