import { lazy, Suspense, useEffect } from "react";
import { Switch, Route, useLocation } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { ThemeProvider } from "@/components/theme-provider";
import { ErrorBoundary } from "@/components/error-boundary";
import { useAuth } from "@/hooks/useAuth";
import AppShell from "@/components/layout/app-shell";
import { PermissionGuard } from "@/components/auth/permission-guard";
import { AgentProvider } from "@/contexts/agent-context";
import { WhitelabelProvider } from "@/contexts/whitelabel-context";
import { ResourceHints } from "@/components/resource-hints";
import { useServiceWorker } from "@/hooks/useServiceWorker";

// Eagerly load critical pages
import Landing from "@/pages/landing";
import Dashboard from "@/pages/dashboard";
import NotFound from "@/pages/not-found";

// Lazy load secondary pages
const Agents = lazy(() => import("@/pages/agents"));
const Voices = lazy(() => import("@/pages/voices"));
const History = lazy(() => import("@/pages/history"));
const Integrations = lazy(() => import("@/pages/integrations"));
const Billing = lazy(() => import("@/pages/billing"));
const Settings = lazy(() => import("@/pages/settings"));
const Admin = lazy(() => import("@/pages/admin-new"));
// Removed Checkout - using unified-checkout component instead
const Playground = lazy(() => import("@/pages/playground"));
const PhoneNumbers = lazy(() => import("@/pages/phone-numbers"));
const OutboundCalling = lazy(() => import("@/pages/outbound-calling"));
const Tools = lazy(() => import("@/pages/tools"));
const KnowledgeBase = lazy(() => import("@/pages/knowledge-base"));
const AgentSettings = lazy(() => import("@/pages/agent-settings"));
const AgentTesting = lazy(() => import("@/pages/agent-testing"));
const WhitelabelSettings = lazy(() => import("@/pages/whitelabel-settings"));
const AgencyUsers = lazy(() => import("@/pages/agency-users"));
const AgencyBillingSettings = lazy(() => import("@/pages/agency-billing-settings"));
// Removed AgencyCheckout - using unified-checkout component instead

// Loading fallback component with elegant brand gradient ring
// Loading fallback component with elegant brand gradient ring
function PageLoader() {
  return (
    <div className="flex items-center justify-center h-64">
      <div className="relative w-16 h-16">
        <div className="absolute inset-0 rounded-full border-4 border-secondary/30"></div>
        <div
          className="absolute inset-0 rounded-full border-4 border-transparent animate-spin"
          style={{
            borderTopColor: 'hsl(var(--primary))',
            borderRightColor: 'hsl(var(--primary))',
            filter: 'drop-shadow(0 0 8px hsl(var(--primary) / 0.4))'
          }}
        ></div>
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="w-2 h-2 bg-primary rounded-full animate-pulse"></div>
        </div>
      </div>
    </div>
  );
}

// Agency-specific routing wrapper
function AgencyRouter() {
  const { isAuthenticated, isLoading } = useAuth();

  // Show loading spinner while authentication is being checked
  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-background">
        <div className="relative w-16 h-16">
          <div className="absolute inset-0 rounded-full border-4 border-gray-200 dark:border-gray-700"></div>
          <div
            className="absolute inset-0 rounded-full border-4 border-transparent animate-spin"
            style={{
              borderTopColor: 'var(--primary)',
              borderRightColor: 'var(--primary)',
              filter: 'drop-shadow(0 0 12px color-mix(in hsl, var(--primary) 50%, transparent))'
            }}
          ></div>
        </div>
      </div>
    );
  }

  // Redirect to landing page if not authenticated
  if (!isAuthenticated) {
    return <Landing />;
  }

  return (
    <AgentProvider>
      <AppShell>
        <Suspense fallback={<PageLoader />}>
          <Switch>
            <Route path="/agency/:subdomain" component={Dashboard} />
            <Route path="/agency/:subdomain/agents">
              <PermissionGuard><Agents /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/agents/:id">
              <PermissionGuard><AgentSettings /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/agent-settings">
              <PermissionGuard><AgentSettings /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/agent-testing" component={AgentTesting} />
            <Route path="/agency/:subdomain/voices">
              <PermissionGuard><Voices /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/phone-numbers">
              <PermissionGuard><PhoneNumbers /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/outbound-calling">
              <PermissionGuard><OutboundCalling /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/tools">
              <PermissionGuard><Tools /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/knowledge-base">
              <PermissionGuard><KnowledgeBase /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/playground">
              <PermissionGuard><Playground /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/history">
              <PermissionGuard><History /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/integrations">
              <PermissionGuard><Integrations /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/billing">
              <PermissionGuard><Billing /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/settings" component={Settings} />
            <Route path="/agency/:subdomain/admin">
              <PermissionGuard permission="manage_users"><Admin /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/whitelabel-settings">
              <PermissionGuard><WhitelabelSettings /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/agency-users">
              <PermissionGuard><AgencyUsers /></PermissionGuard>
            </Route>
            <Route path="/agency/:subdomain/agency-billing-settings">
              <PermissionGuard><AgencyBillingSettings /></PermissionGuard>
            </Route>
          </Switch>
        </Suspense>
      </AppShell>
    </AgentProvider>
  );
}

function Router() {
  const { isAuthenticated, isLoading } = useAuth();
  const [location] = useLocation();
  const isAgencyPath = location.startsWith('/agency/');

  // For agency paths, use the agency router
  if (isAgencyPath) {
    return <AgencyRouter />;
  }

  // Show loading spinner while authentication is being checked
  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-background">
        <div className="relative w-16 h-16">
          <div className="absolute inset-0 rounded-full border-4 border-gray-200 dark:border-gray-700"></div>
          <div
            className="absolute inset-0 rounded-full border-4 border-transparent animate-spin"
            style={{
              borderTopColor: 'var(--primary)',
              borderRightColor: 'var(--primary)',
              filter: 'drop-shadow(0 0 12px color-mix(in hsl, var(--primary) 50%, transparent))'
            }}
          ></div>
        </div>
      </div>
    );
  }

  // Redirect to landing page if not authenticated
  if (!isAuthenticated) {
    return <Landing />;
  }

  return (
    <AgentProvider>
      <AppShell>
        <Suspense fallback={<PageLoader />}>
          <Switch>
            <Route path="/" component={Dashboard} />
            <Route path="/agents">
              <PermissionGuard><Agents /></PermissionGuard>
            </Route>
            <Route path="/agents/:id">
              <PermissionGuard><AgentSettings /></PermissionGuard>
            </Route>
            <Route path="/agent-settings">
              <PermissionGuard><AgentSettings /></PermissionGuard>
            </Route>
            <Route path="/agent-testing" component={AgentTesting} />
            <Route path="/voices">
              <PermissionGuard><Voices /></PermissionGuard>
            </Route>
            <Route path="/phone-numbers">
              <PermissionGuard><PhoneNumbers /></PermissionGuard>
            </Route>
            <Route path="/outbound-calling">
              <PermissionGuard><OutboundCalling /></PermissionGuard>
            </Route>
            <Route path="/tools">
              <PermissionGuard><Tools /></PermissionGuard>
            </Route>
            <Route path="/knowledge-base">
              <PermissionGuard><KnowledgeBase /></PermissionGuard>
            </Route>
            <Route path="/playground">
              <PermissionGuard><Playground /></PermissionGuard>
            </Route>
            <Route path="/history">
              <PermissionGuard><History /></PermissionGuard>
            </Route>
            <Route path="/integrations">
              <PermissionGuard><Integrations /></PermissionGuard>
            </Route>
            <Route path="/billing">
              <PermissionGuard><Billing /></PermissionGuard>
            </Route>
            <Route path="/settings" component={Settings} />
            <Route path="/admin">
              <PermissionGuard permission="manage_users"><Admin /></PermissionGuard>
            </Route>
            <Route path="/whitelabel-settings">
              <PermissionGuard><WhitelabelSettings /></PermissionGuard>
            </Route>
            <Route path="/agency-users">
              <PermissionGuard><AgencyUsers /></PermissionGuard>
            </Route>
            <Route path="/agency-billing-settings">
              <PermissionGuard><AgencyBillingSettings /></PermissionGuard>
            </Route>
            <Route component={NotFound} />
          </Switch>
        </Suspense>
      </AppShell>
    </AgentProvider>
  );
}

function App() {
  // Register service worker for offline support and caching
  useServiceWorker();

  // Prefetch critical resources
  useEffect(() => {
    // Prefetch common routes when idle
    if ('requestIdleCallback' in window) {
      requestIdleCallback(() => {
        // Prefetch common chunks
        const commonRoutes = ['/agents', '/history', '/settings'];
        commonRoutes.forEach(route => {
          const link = document.createElement('link');
          link.rel = 'prefetch';
          link.href = route;
          document.head.appendChild(link);
        });
      });
    }
  }, []);

  return (
    <ErrorBoundary>
      <QueryClientProvider client={queryClient}>
        <ThemeProvider>
          <WhitelabelProvider>
            <TooltipProvider>
              {/* Add resource hints for better performance */}
              <ResourceHints
                preconnect={[
                  'https://api.elevenlabs.io',
                  'https://js.stripe.com',
                ]}
                dns={[
                  'https://www.googletagmanager.com',
                  'https://www.google-analytics.com',
                ]}
              />
              <Toaster />
              <ErrorBoundary>
                <Router />
              </ErrorBoundary>
            </TooltipProvider>
          </WhitelabelProvider>
        </ThemeProvider>
      </QueryClientProvider>
    </ErrorBoundary>
  );
}

export default App;

client/src/pages/admin/approval-tasks.ts.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { 
  Shield, CheckCircle, XCircle, AlertCircle, Clock, 
  RefreshCw, Eye, CheckSquare, XSquare, MessageSquare,
  Building2, Calendar, FileText, Loader2, Webhook, Plus,
  Edit, Trash2, Settings, Send
} from "lucide-react";
import { Input } from "@/components/ui/input";
import { Switch } from "@/components/ui/switch";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogFooter } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";

interface AdminTask {
  id: string;
  type: "approval" | "review" | "action";
  title: string;
  description: string;
  status: "pending" | "in_progress" | "completed" | "rejected";
  priority: "low" | "medium" | "high" | "urgent";
  relatedEntityType: "integration" | "webhook" | "agent" | "organization" | "mcp" | "platform_webhook";
  relatedEntityId: string;
  createdBy: string;
  approvedBy?: string;
  metadata?: Record<string, any>;
  createdAt: string;
  updatedAt?: string;
  completedAt?: string;
  rejectionReason?: string;
}

interface ApprovalWebhook {
  id: string;
  name: string;
  description?: string;
  webhookUrl: string;
  secret?: string;
  events: string[];
  isActive: boolean;
  headers?: Record<string, string>;
  lastTriggered?: string;
  failureCount?: number;
  createdAt: string;
  updatedAt?: string;
}

export default function ApprovalTasks() {
  const { toast } = useToast();
  const [selectedTask, setSelectedTask] = useState<AdminTask | null>(null);
  const [rejectDialog, setRejectDialog] = useState<{ isOpen: boolean; task: AdminTask | null }>({
    isOpen: false,
    task: null
  });
  const [rejectionReason, setRejectionReason] = useState("");
  const [webhookDialog, setWebhookDialog] = useState<{ 
    isOpen: boolean; 
    webhook: ApprovalWebhook | null;
  }>({ isOpen: false, webhook: null });
  const [testWebhookId, setTestWebhookId] = useState<string | null>(null);

  // Fetch pending tasks
  const { data: tasks = [], isLoading } = useQuery<AdminTask[]>({
    queryKey: ["/api/admin/tasks"],
  });

  // Fetch webhooks
  const { data: webhooks = [] } = useQuery<ApprovalWebhook[]>({
    queryKey: ["/api/admin/approval-webhooks"],
  });

  // Approve task mutation
  const approveMutation = useMutation({
    mutationFn: async (taskId: string) => {
      return await apiRequest("POST", `/api/admin/tasks/${taskId}/approve`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/tasks"] });
      toast({ 
        title: "Task Approved", 
        description: "The integration has been approved and activated",
      });
      setSelectedTask(null);
    },
    onError: () => {
      toast({ 
        title: "Approval Failed", 
        description: "Failed to approve the task",
        variant: "destructive" 
      });
    },
  });

  // Reject task mutation
  const rejectMutation = useMutation({
    mutationFn: async ({ taskId, reason }: { taskId: string; reason: string }) => {
      return await apiRequest("POST", `/api/admin/tasks/${taskId}/reject`, { reason });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/tasks"] });
      toast({ 
        title: "Task Rejected", 
        description: "The integration request has been rejected",
      });
      setRejectDialog({ isOpen: false, task: null });
      setRejectionReason("");
      setSelectedTask(null);
    },
    onError: () => {
      toast({ 
        title: "Rejection Failed", 
        description: "Failed to reject the task",
        variant: "destructive" 
      });
    },
  });

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "pending":
        return (
          <Badge className="bg-amber-100 dark:bg-amber-900 text-amber-800 dark:text-amber-200">
            <Clock className="w-3 h-3 mr-1" />
            Pending
          </Badge>
        );
      case "in_progress":
        return (
          <Badge className="bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200">
            <RefreshCw className="w-3 h-3 mr-1" />
            In Progress
          </Badge>
        );
      case "completed":
        return (
          <Badge className="bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200">
            <CheckCircle className="w-3 h-3 mr-1" />
            Completed
          </Badge>
        );
      case "rejected":
        return (
          <Badge className="bg-red-100 dark:bg-red-900 text-red-800 dark:text-red-200">
            <XCircle className="w-3 h-3 mr-1" />
            Rejected
          </Badge>
        );
      default:
        return null;
    }
  };

  const getPriorityBadge = (priority: string) => {
    switch (priority) {
      case "urgent":
        return <Badge variant="destructive">Urgent</Badge>;
      case "high":
        return <Badge className="bg-orange-500">High</Badge>;
      case "medium":
        return <Badge className="bg-yellow-500">Medium</Badge>;
      case "low":
        return <Badge variant="secondary">Low</Badge>;
      default:
        return null;
    }
  };

  const getEntityIcon = (type: string) => {
    switch (type) {
      case "integration":
        return <Shield className="w-4 h-4" />;
      case "webhook":
        return <FileText className="w-4 h-4" />;
      case "agent":
        return <MessageSquare className="w-4 h-4" />;
      case "organization":
        return <Building2 className="w-4 h-4" />;
      case "mcp":
        return <Settings className="w-4 h-4" />;
      case "platform_webhook":
        return <Webhook className="w-4 h-4" />;
      default:
        return null;
    }
  };

  // Group tasks by status
  const pendingTasks = tasks.filter(t => t.status === "pending");
  const inProgressTasks = tasks.filter(t => t.status === "in_progress");
  const completedTasks = tasks.filter(t => t.status === "completed" || t.status === "rejected");

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <Loader2 className="w-8 h-8 animate-spin text-muted-foreground" />
      </div>
    );
  }

  // For now, since the database isn't ready, show a placeholder
  if (tasks.length === 0) {
    return (
      <Card className="p-8">
        <div className="flex flex-col items-center justify-center space-y-4">
          <Shield className="w-12 h-12 text-muted-foreground" />
          <div className="text-center">
            <h3 className="text-lg font-semibold mb-2">No Approval Tasks</h3>
            <p className="text-sm text-muted-foreground max-w-md">
              When users configure MCP services, webhooks, platform integrations, or other tools that require approval, they will appear here for your review.
            </p>
          </div>
        </div>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-xl font-semibold">Approval Tasks</h2>
          <p className="text-sm text-muted-foreground mt-1">
            Review and approve MCP services, webhooks, platform integrations, and other tools configurations
          </p>
        </div>
        <Badge variant="outline" className="text-lg px-4 py-2">
          {pendingTasks.length} Pending
        </Badge>
      </div>

      <Tabs defaultValue="pending" className="w-full">
        <TabsList>
          <TabsTrigger value="pending">
            Pending ({pendingTasks.length})
          </TabsTrigger>
          <TabsTrigger value="in-progress">
            In Progress ({inProgressTasks.length})
          </TabsTrigger>
          <TabsTrigger value="completed">
            Completed ({completedTasks.length})
          </TabsTrigger>
          <TabsTrigger value="webhooks">
            <Webhook className="w-4 h-4 mr-1" />
            Webhooks
          </TabsTrigger>
        </TabsList>

        <TabsContent value="pending" className="space-y-4">
          {pendingTasks.length === 0 ? (
            <Card className="p-8">
              <div className="text-center text-muted-foreground">
                <p>No pending approval tasks</p>
                <p className="text-sm mt-2">When users configure MCP services, webhooks, or platform integrations that require approval, they will appear here.</p>
              </div>
            </Card>
          ) : (
            pendingTasks.map((task) => (
              <Card key={task.id} className="p-6">
                <div className="flex items-start justify-between mb-4">
                  <div className="flex items-start gap-3">
                    <div className="p-2 bg-muted rounded-lg">
                      {getEntityIcon(task.relatedEntityType)}
                    </div>
                    <div>
                      <h3 className="font-semibold">{task.title}</h3>
                      <p className="text-sm text-muted-foreground mt-1">{task.description}</p>
                    </div>
                  </div>
                  <div className="flex flex-col gap-2 items-end">
                    {getStatusBadge(task.status)}
                    {getPriorityBadge(task.priority)}
                  </div>
                </div>

                {task.metadata && (
                  <div className="bg-muted/50 rounded-lg p-4 mb-4">
                    <div className="grid grid-cols-2 gap-2 text-sm">
                      {task.metadata.organizationId && (
                        <div>
                          <span className="text-muted-foreground">Organization:</span>
                          <span className="ml-2 font-medium">{task.metadata.organizationName || task.metadata.organizationId}</span>
                        </div>
                      )}
                      {task.metadata.provider && (
                        <div>
                          <span className="text-muted-foreground">Provider:</span>
                          <span className="ml-2 font-medium capitalize">{task.metadata.provider}</span>
                        </div>
                      )}
                      {task.metadata.userEmail && (
                        <div>
                          <span className="text-muted-foreground">Requested by:</span>
                          <span className="ml-2 font-medium">{task.metadata.userEmail}</span>
                        </div>
                      )}
                      <div>
                        <span className="text-muted-foreground">Created:</span>
                        <span className="ml-2 font-medium">
                          {new Date(task.createdAt).toLocaleDateString()}
                        </span>
                      </div>
                    </div>
                  </div>
                )}

                <div className="flex gap-3">
                  <Button
                    onClick={() => approveMutation.mutate(task.id)}
                    disabled={approveMutation.isPending}
                    className="bg-green-600 hover:bg-green-700"
                  >
                    {approveMutation.isPending ? (
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    ) : (
                      <CheckSquare className="w-4 h-4 mr-2" />
                    )}
                    Approve
                  </Button>
                  <Button
                    variant="destructive"
                    onClick={() => setRejectDialog({ isOpen: true, task })}
                    disabled={rejectMutation.isPending}
                  >
                    <XSquare className="w-4 h-4 mr-2" />
                    Reject
                  </Button>
                  <Button
                    variant="outline"
                    onClick={() => setSelectedTask(task)}
                  >
                    <Eye className="w-4 h-4 mr-2" />
                    View Details
                  </Button>
                </div>
              </Card>
            ))
          )}
        </TabsContent>

        <TabsContent value="in-progress" className="space-y-4">
          {inProgressTasks.length === 0 ? (
            <Card className="p-8">
              <div className="text-center text-muted-foreground">
                No tasks in progress
              </div>
            </Card>
          ) : (
            inProgressTasks.map((task) => (
              <Card key={task.id} className="p-6 opacity-75">
                <div className="flex items-start justify-between">
                  <div>
                    <h3 className="font-semibold">{task.title}</h3>
                    <p className="text-sm text-muted-foreground mt-1">{task.description}</p>
                  </div>
                  {getStatusBadge(task.status)}
                </div>
              </Card>
            ))
          )}
        </TabsContent>

        <TabsContent value="completed" className="space-y-4">
          {completedTasks.length === 0 ? (
            <Card className="p-8">
              <div className="text-center text-muted-foreground">
                No completed tasks
              </div>
            </Card>
          ) : (
            completedTasks.map((task) => (
              <Card key={task.id} className="p-6 opacity-75">
                <div className="flex items-start justify-between">
                  <div>
                    <h3 className="font-semibold">{task.title}</h3>
                    <p className="text-sm text-muted-foreground mt-1">{task.description}</p>
                    {task.rejectionReason && (
                      <p className="text-sm text-red-600 dark:text-red-400 mt-2">
                        Rejection reason: {task.rejectionReason}
                      </p>
                    )}
                  </div>
                  <div className="flex flex-col gap-2 items-end">
                    {getStatusBadge(task.status)}
                    {task.completedAt && (
                      <span className="text-xs text-muted-foreground">
                        {new Date(task.completedAt).toLocaleDateString()}
                      </span>
                    )}
                  </div>
                </div>
              </Card>
            ))
          )}
        </TabsContent>

        <TabsContent value="webhooks" className="space-y-4">
          <Card className="p-6">
            <div className="flex justify-between items-center mb-4">
              <div>
                <h3 className="text-lg font-semibold">Webhook Configuration</h3>
                <p className="text-sm text-muted-foreground mt-1">
                  Configure webhooks to receive notifications when approval tasks are created or updated
                </p>
              </div>
              <Button 
                onClick={() => setWebhookDialog({ isOpen: true, webhook: null })}
                className="gap-2"
              >
                <Plus className="w-4 h-4" />
