import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Bot, Plus, Trash2, Play, RefreshCw, Settings, FlaskConical, Sparkles } from "lucide-react";
import { AddAgentModal } from "@/components/modals/add-agent-modal";
import { AgentDetailModal } from "@/components/modals/agent-detail-modal";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";
import { cn } from "@/lib/utils";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import type { Agent } from "@shared/schema";

export default function Agents() {
  const [location, setLocation] = useLocation();
  const [showAddModal, setShowAddModal] = useState(false);
  const [selectedAgent, setSelectedAgent] = useState<Agent | null>(null);
  const [agentToDelete, setAgentToDelete] = useState<Agent | null>(null);
  const { toast } = useToast();

  const { data: agents, isLoading } = useQuery<Agent[]>({
    queryKey: ["/api/agents"],
  });

  // Delete mutation
  const deleteMutation = useMutation({
    mutationFn: async (agentId: string) => {
      const response = await fetch(`/api/agents/${agentId}`, {
        method: "DELETE",
        credentials: "include",
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to delete agent");
      }

      return await response.json();
    },
    onSuccess: () => {
      toast({
        title: "Agent Removed",
        description: "The agent has been successfully removed.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/agents"] });
      queryClient.invalidateQueries({ queryKey: ["/api/call-logs"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/organization"] });
      setAgentToDelete(null);
    },
    onError: (error) => {
      toast({
        title: "Failed to Remove Agent",
        description: error.message || "Could not remove the agent. Please try again.",
        variant: "destructive",
      });
      setAgentToDelete(null);
    },
  });

  // Sync mutation
  const syncMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch("/api/agents/sync", {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to sync agents");
      }

      return await response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Sync Completed",
        description: `Successfully synced ${data.syncedCount} agents (${data.createdCount} new, ${data.updatedCount} updated)`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/agents"] });
      queryClient.invalidateQueries({ queryKey: ["/api/call-logs"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/organization"] });
    },
    onError: (error) => {
      toast({
        title: "Sync Failed",
        description: error.message || "Could not sync agents with the voice service",
        variant: "destructive",
      });
    },
  });

  const getStatusText = (isActive: boolean) => {
    return isActive ? "Active" : "Inactive";
  };

  if (isLoading) {
    return (
      <div className="space-y-8 animate-in fade-in duration-500">
        <div className="flex flex-col sm:flex-row sm:justify-between gap-4">
          <div className="h-8 w-48 bg-gray-200 dark:bg-gray-800 rounded animate-pulse" />
          <div className="h-10 w-full sm:w-32 bg-gray-200 dark:bg-gray-800 rounded animate-pulse" />
        </div>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {[...Array(6)].map((_, i) => (
            <div key={i} className="h-72 bg-gray-200 dark:bg-gray-800 rounded-xl animate-pulse" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <TooltipProvider>
      <div className="space-y-8 animate-in fade-in duration-500">
        <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center gap-6">
          <div className="space-y-1">
            <h2 className="text-3xl font-bold tracking-tight bg-clip-text text-transparent bg-gradient-to-r from-gray-900 to-gray-600 dark:from-white dark:to-gray-400" data-testid="text-page-title">
              Voice Agents
            </h2>
            <p className="text-muted-foreground text-lg" data-testid="text-page-description">
              Manage and monitor your conversational AI assistants
            </p>
          </div>
          <div className="flex flex-col sm:flex-row gap-3">
            <Button
              onClick={() => syncMutation.mutate()}
              variant="outline"
              disabled={syncMutation.isPending}
              className="w-full sm:w-auto border-gray-200 dark:border-gray-800 hover:bg-gray-100 dark:hover:bg-gray-800"
              data-testid="button-sync-agents"
            >
              <RefreshCw className={`w-4 h-4 mr-2 ${syncMutation.isPending ? 'animate-spin' : ''}`} />
              {syncMutation.isPending ? 'Syncing...' : 'Sync Agents'}
            </Button>
            <Button
              onClick={() => setShowAddModal(true)}
              className="w-full sm:w-auto btn-brand-premium shadow-lg shadow-primary/20"
              data-testid="button-add-agent"
            >
              <Plus className="w-4 h-4 mr-2" />
              New Agent
            </Button>
          </div>
        </div>

        {/* Agents Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {!agents || agents.length === 0 ? (
            <Card className="col-span-full p-16 border-dashed border-2 bg-gray-50/50 dark:bg-gray-900/50">
              <div className="text-center space-y-6">
                <div className="w-20 h-20 mx-auto rounded-full bg-primary/10 flex items-center justify-center ring-8 ring-primary/5">
                  <Bot className="w-10 h-10 text-primary" />
                </div>
                <div className="space-y-2">
                  <h3 className="text-2xl font-semibold" data-testid="text-no-agents-title">
                    No agents configured
                  </h3>
                  <p className="text-muted-foreground max-w-lg mx-auto text-lg" data-testid="text-no-agents-description">
                    Get started by creating your first voice agent to handle conversations automatically.
                  </p>
                </div>

                <Button onClick={() => setShowAddModal(true)} data-testid="button-add-first-agent" size="lg" className="btn-brand-premium px-8">
                  <Sparkles className="w-4 h-4 mr-2" />
                  Create First Agent
                </Button>
              </div>
            </Card>
          ) : (
            agents.map((agent) => (
              <Card
                key={agent.id}
                className="group relative flex flex-col h-full border-0 shadow-lg hover:shadow-2xl transition-all duration-300 cursor-pointer overflow-hidden bg-white dark:bg-gray-900 ring-1 ring-gray-200 dark:ring-gray-800 hover:ring-primary/50 hover:-translate-y-1"
                onClick={() => setSelectedAgent(agent)}
              >
                {/* Card Header Gradient */}
                <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-primary to-purple-600 opacity-0 group-hover:opacity-100 transition-opacity duration-300" />

                <div className="p-6 flex-1 flex flex-col">
                  <div className="flex items-start justify-between mb-6">
                    <div className="w-14 h-14 rounded-2xl bg-gradient-to-br from-gray-100 to-gray-200 dark:from-gray-800 dark:to-gray-900 flex items-center justify-center shadow-inner group-hover:scale-105 transition-transform duration-300">
                      <Bot className="w-7 h-7 text-primary" />
                    </div>
                    <Badge className={cn(
                      "px-3 py-1 rounded-full text-xs font-medium transition-colors",
                      agent.isActive
                        ? "bg-emerald-500/10 text-emerald-600 dark:text-emerald-400 border-emerald-500/20"
                        : "bg-yellow-500/10 text-yellow-600 dark:text-yellow-400 border-yellow-500/20"
                    )} data-testid={`badge-status-${agent.id}`}>
                      <span className={cn("w-1.5 h-1.5 rounded-full mr-2 inline-block", agent.isActive ? "bg-emerald-500" : "bg-yellow-500")} />
                      {getStatusText(agent.isActive)}
                    </Badge>
                  </div>

                  <div className="space-y-2 mb-6">
                    <h3 className="text-xl font-bold text-gray-900 dark:text-white group-hover:text-primary transition-colors" data-testid={`text-agent-name-${agent.id}`}>
                      {agent.name}
                    </h3>

                    {agent.description && (
                      <p className="text-sm text-muted-foreground line-clamp-2 leading-relaxed" data-testid={`text-agent-description-${agent.id}`}>
                        {agent.description}
                      </p>
                    )}
                  </div>

                  <div className="mt-auto space-y-4">
                    <div className="flex items-center justify-between text-xs text-muted-foreground bg-gray-50 dark:bg-gray-800/50 p-2 rounded-lg">
                      <span>Agent ID</span>
                      <code className="font-mono font-medium text-primary/80">{agent.externalAgentId?.slice(0, 8)}...</code>
                    </div>

                    {agent.providers && Object.keys(agent.providers).length > 0 && (
                      <div className="flex flex-wrap gap-1.5">
                        {agent.providers.llm && (
                          <Badge variant="secondary" className="text-[10px] bg-blue-50 text-blue-700 dark:bg-blue-900/20 dark:text-blue-300 border-blue-200 dark:border-blue-800">
                            {agent.providers.llm}
                          </Badge>
                        )}
                        {agent.providers.tts && (
                          <Badge variant="secondary" className="text-[10px] bg-purple-50 text-purple-700 dark:bg-purple-900/20 dark:text-purple-300 border-purple-200 dark:border-purple-800">
                            {agent.providers.tts}
                          </Badge>
                        )}
                      </div>
                    )}
                  </div>
                </div>

                {/* Action Footer */}
                <div className="p-4 bg-gray-50 dark:bg-gray-800/50 border-t border-gray-100 dark:border-gray-800 flex gap-2 opacity-0 group-hover:opacity-100 transition-all duration-300 translate-y-2 group-hover:translate-y-0">
                  <Button
                    variant="ghost"
                    size="sm"
                    className="flex-1 hover:bg-white dark:hover:bg-gray-700 shadow-sm"
                    onClick={(e) => {
                      e.stopPropagation();
                      setLocation(`/agents/${agent.id}`);
                    }}
                  >
                    <Settings className="w-4 h-4 mr-2" />
                    Settings
                  </Button>
                  <Button
                    size="sm"
                    className="flex-1 btn-brand-premium"
                    onClick={(e) => {
                      e.stopPropagation();
                      setLocation(`/agent-testing?agentId=${agent.id}`);
                    }}
                  >
                    <FlaskConical className="w-4 h-4 mr-2" />
                    Test
                  </Button>
                  <Button
                    variant="ghost"
                    size="icon"
                    className="text-muted-foreground hover:text-red-600 hover:bg-red-50 dark:hover:bg-red-900/20"
                    onClick={(e) => {
                      e.stopPropagation();
                      setAgentToDelete(agent);
                    }}
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              </Card>
            ))
          )}
        </div>

        <AddAgentModal
          open={showAddModal}
          onOpenChange={setShowAddModal}
        />

        <AgentDetailModal
          agent={selectedAgent}
          open={!!selectedAgent}
          onOpenChange={(open) => !open && setSelectedAgent(null)}
        />

        {/* Delete Confirmation Dialog */}
        <AlertDialog open={!!agentToDelete} onOpenChange={(open) => !open && setAgentToDelete(null)}>
          <AlertDialogContent>
            <AlertDialogHeader>
              <AlertDialogTitle>Remove Agent</AlertDialogTitle>
              <AlertDialogDescription>
                Are you sure you want to remove <strong>{agentToDelete?.name}</strong>?
                This action cannot be undone. Call logs associated with this agent will be preserved.
              </AlertDialogDescription>
            </AlertDialogHeader>
            <AlertDialogFooter>
              <AlertDialogCancel>Cancel</AlertDialogCancel>
              <AlertDialogAction
                onClick={() => agentToDelete && deleteMutation.mutate(agentToDelete.id)}
                disabled={deleteMutation.isPending}
                className="bg-red-600 hover:bg-red-700"
              >
                {deleteMutation.isPending ? "Removing..." : "Remove Agent"}
              </AlertDialogAction>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialog>
      </div>
    </TooltipProvider>
  );
}

server/middleware/cache-middleware.ts.content:
import { Request, Response, NextFunction } from 'express';
import { cacheManagers, CacheManager } from '../cache/cache-manager';

interface CacheOptions {
  ttl?: number;           // Time to live in milliseconds
  key?: string | ((req: Request) => string);
  condition?: (req: Request) => boolean;
  invalidatePattern?: string;
  cacheManager?: CacheManager; // Add cacheManager to interface
}

// Default cache key generator
const defaultKeyGenerator = (req: Request): string => {
  const user = (req as any).user;
  const userId = user?.id || 'anonymous';
  const organizationId = user?.organizationId || 'global';

  // Include query params in cache key
  const queryString = Object.keys(req.query).length > 0 
    ? '?' + new URLSearchParams(req.query as any).toString()
    : '';

  return `api:${organizationId}:${userId}:${req.path}${queryString}`;
};

// Create cache middleware with improved caching strategies
export function createCacheMiddleware(options: CacheOptions = {}) {
  const {
    ttl = 5 * 60 * 1000,  // 5 minutes default (increased from 1)
    key,
    condition = () => true,
    invalidatePattern,
    cacheManager = cacheManagers.default() // Allow custom cache manager
  } = options;

  return async (req: Request, res: Response, next: NextFunction) => {
    // Skip caching for non-GET requests
    if (req.method !== 'GET') {
      // If it's a mutation, invalidate related cache
      if (invalidatePattern && ['POST', 'PUT', 'PATCH', 'DELETE'].includes(req.method)) {
        const pattern = invalidatePattern.replace('{org}', (req as any).user?.organizationId || '*');
        await cacheManager.invalidate(pattern);
      }
      return next();
    }

    // Check condition
    if (!condition(req)) {
      return next();
    }

    // Generate cache key
    const cacheKey = typeof key === 'function' 
      ? key(req) 
      : typeof key === 'string' 
        ? key 
        : defaultKeyGenerator(req);

    // Use stale-while-revalidate for better performance
    const useStaleWhileRevalidate = ttl > 60000; // Use for caches > 1 minute

    if (useStaleWhileRevalidate) {
      // Try stale-while-revalidate pattern
      try {
        const data = await cacheManager.getStaleWhileRevalidate(
          cacheKey,
          async () => {
            // This will be called if cache miss or background revalidation
            return new Promise((resolve, reject) => {
              const originalJson = res.json;
              res.json = function(data: any) {
                resolve(data);
                return originalJson.call(this, data);
              };
              // Continue with request handling
              next();
            });
          },
          { ttl }
        );

        // Only set headers if response hasn't been sent yet
        if (!res.headersSent) {
          res.setHeader('X-Cache', 'HIT-STALE');
          res.setHeader('X-Cache-TTL', ttl.toString());
          return res.json(data);
        }
        return;
      } catch (error) {
        // Fall through to normal handling
      }
    }

    // Try to get from cache (normal flow)
    const cached = await cacheManager.get(cacheKey);
    if (cached) {
      // Add cache hit header
      res.setHeader('X-Cache', 'HIT');
      res.setHeader('X-Cache-TTL', ttl.toString());
      res.setHeader('Cache-Control', `private, max-age=${Math.floor(ttl / 1000)}`);
      return res.json(cached);
    }

    // Cache miss - capture response and cache it
    const originalJson = res.json;
    res.json = function(data: any) {
      // Add cache headers
      res.setHeader('X-Cache', 'MISS');
      res.setHeader('X-Cache-TTL', ttl.toString());
      res.setHeader('Cache-Control', `private, max-age=${Math.floor(ttl / 1000)}`);

      // Cache successful responses only
      if (res.statusCode >= 200 && res.statusCode < 300) {
        cacheManager.set(cacheKey, data, { ttl }).catch(() => {
          // Silent fail - caching is not critical
        });
      }

      return originalJson.call(this, data);
    };

    next();
  };
}

// Pre-configured cache middleware with optimized TTLs
export const cacheMiddleware = {
  // Short cache for frequently changing data
  short: createCacheMiddleware({
    ttl: 60 * 1000,  // 1 minute (increased from 30s)
    cacheManager: cacheManagers.default()
  }),

  // Standard cache for normal API responses
  standard: createCacheMiddleware({
    ttl: 5 * 60 * 1000,  // 5 minutes (increased from 1)
    cacheManager: cacheManagers.default()
  }),

  // Long cache for rarely changing data
  long: createCacheMiddleware({
    ttl: 30 * 60 * 1000,  // 30 minutes (increased from 5)
    cacheManager: cacheManagers.static()
  }),

  // Very long cache for static data
  static: createCacheMiddleware({
    ttl: 2 * 60 * 60 * 1000,  // 2 hours (increased from 1)
    cacheManager: cacheManagers.static()
  }),

  // Organization-specific cache
  organization: createCacheMiddleware({
    ttl: 10 * 60 * 1000,  // 10 minutes (increased from 2)
    key: (req) => {
      const user = (req as any).user;
      return `org:${user?.organizationId}:${req.path}`;
    },
    cacheManager: cacheManagers.default()
  }),

  // User-specific cache
  user: createCacheMiddleware({
    ttl: 5 * 60 * 1000,  // 5 minutes (increased from 1)
    key: (req) => {
      const user = (req as any).user;
      return `user:${user?.id}:${req.path}`;
    },
    cacheManager: cacheManagers.user()
  }),

  // Analytics cache (longer TTL)
  analytics: createCacheMiddleware({
    ttl: 30 * 60 * 1000,  // 30 minutes (increased from 10)
    key: (req) => {
      const user = (req as any).user;
      const params = new URLSearchParams(req.query as any).toString();
      return `analytics:${user?.organizationId}:${params}`;
    },
    cacheManager: cacheManagers.analytics()
  }),

  // Agent list cache
  agents: createCacheMiddleware({
    ttl: 10 * 60 * 1000,  // 10 minutes (increased from 2)
    key: (req) => {
      const user = (req as any).user;
      return `agents:${user?.organizationId}:${user?.id}`;
    },
    invalidatePattern: 'agents:{org}:*',
    cacheManager: cacheManagers.agents()
  }),

  // Call logs cache
  callLogs: createCacheMiddleware({
    ttl: 5 * 60 * 1000,  // 5 minutes (increased from 1)
    key: (req) => {
      const user = (req as any).user;
      const params = new URLSearchParams(req.query as any).toString();
      return `call-logs:${user?.organizationId}:${params}`;
    },
    invalidatePattern: 'call-logs:{org}:*',
    cacheManager: cacheManagers.default()
  })
};

// Cache invalidation helper for all cache managers
export async function invalidateCache(pattern: string, organizationId?: string) {
  const finalPattern = pattern.replace('{org}', organizationId || '*');

  // Invalidate across all cache managers
  await Promise.all([
    cacheManagers.default().invalidate(finalPattern),
    cacheManagers.agents().invalidate(finalPattern),
    cacheManagers.analytics().invalidate(finalPattern),
    cacheManagers.user().invalidate(finalPattern),
    cacheManagers.static().invalidate(finalPattern)
  ]);
}

// Get aggregated cache statistics
export async function getCacheStats() {
  const stats = {
    default: cacheManagers.default().getStats(),
    agents: cacheManagers.agents().getStats(),
    analytics: cacheManagers.analytics().getStats(),
    user: cacheManagers.user().getStats(),
    static: cacheManagers.static().getStats(),
  };

  // Calculate total stats
  const total = {
    hits: 0,
    misses: 0,
    size: 0,
    hitRate: '0.00%'
  };

  Object.values(stats).forEach(stat => {
    total.hits += stat.hits;
    total.misses += stat.misses;
    total.size += stat.size;
  });

  if (total.hits + total.misses > 0) {
    total.hitRate = `${(total.hits / (total.hits + total.misses) * 100).toFixed(2)}%`;
  }

  return { ...stats, total };
}

export default createCacheMiddleware;

client/src/components/webhook-tool-dialo.content:
import { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { Switch } from "@/components/ui/switch";
import { Plus, Trash2, Code, Sparkles, Database } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";
// Simple ID generator
const generateId = () => `webhook_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

interface WebhookTool {
  id: string;
  type: 'webhook';
  name: string;
  description?: string;
  enabled: boolean;
  url: string;
  method: 'GET' | 'POST' | 'PUT' | 'PATCH' | 'DELETE';
  webhookConfig?: {
    responseTimeout?: number;
    disableInterruptions?: boolean;
    preToolSpeech?: 'auto' | 'force' | 'none';
    authentication?: {
      type?: string;
      credentials?: any;
    };
    headers?: Array<{
      key: string;
      value: string;
      enabled: boolean;
    }>;
    pathParameters?: Array<{
      identifier: string;
      description?: string;
    }>;
    queryParameters?: Array<{
      identifier: string;
      description?: string;
      required?: boolean;
      dataType?: 'String' | 'Number' | 'Boolean' | 'Object' | 'Array';
      valueType?: 'LLM Prompt' | 'Static' | 'Dynamic Variable';
    }>;
    bodyParameters?: Array<{
      identifier: string;
      dataType: 'String' | 'Number' | 'Boolean' | 'Object' | 'Array';
      description?: string;
      required?: boolean;
      valueType: 'LLM Prompt' | 'Static' | 'Dynamic Variable';
    }>;
    dynamicVariables?: Record<string, string>;
    dynamicVariableAssignments?: Array<{
      variable: string;
      jsonPath: string;
    }>;
  };
}

interface WebhookToolDialogProps {
  isOpen: boolean;
  onClose: () => void;
  webhook?: WebhookTool;
  onSave: (webhook: WebhookTool) => void;
}

// Webhook templates
const WEBHOOK_TEMPLATES = {
  custom: {
    name: "",
    description: "",
    method: 'POST' as const,
    url: "",
    webhookConfig: {
      responseTimeout: 20,
      disableInterruptions: false,
      preToolSpeech: 'auto' as const
    }
  }
};

export function WebhookToolDialog({ isOpen, onClose, webhook, onSave }: WebhookToolDialogProps) {
  const [showJsonEditor, setShowJsonEditor] = useState(false);
  const [jsonContent, setJsonContent] = useState("");
  const [selectedTemplate, setSelectedTemplate] = useState<string>("");
  const [formData, setFormData] = useState<WebhookTool>({
    id: webhook?.id || generateId(),
    type: 'webhook',
    name: webhook?.name || '',
    description: webhook?.description || '',
    enabled: webhook?.enabled !== false,
    url: webhook?.url || '',
    method: webhook?.method || 'POST',
    webhookConfig: {
      responseTimeout: webhook?.webhookConfig?.responseTimeout || 20,
      disableInterruptions: webhook?.webhookConfig?.disableInterruptions || false,
      preToolSpeech: webhook?.webhookConfig?.preToolSpeech || 'auto',
      authentication: webhook?.webhookConfig?.authentication,
      headers: webhook?.webhookConfig?.headers || [],
      pathParameters: webhook?.webhookConfig?.pathParameters || [],
      queryParameters: webhook?.webhookConfig?.queryParameters || [],
      bodyParameters: webhook?.webhookConfig?.bodyParameters || [],
      dynamicVariables: webhook?.webhookConfig?.dynamicVariables || {},
      dynamicVariableAssignments: webhook?.webhookConfig?.dynamicVariableAssignments || [],
    },
  });

  // Apply template function
  const applyTemplate = (templateName: string) => {
    if (templateName && WEBHOOK_TEMPLATES[templateName as keyof typeof WEBHOOK_TEMPLATES]) {
      const template = WEBHOOK_TEMPLATES[templateName as keyof typeof WEBHOOK_TEMPLATES];
      setFormData({
        ...formData,
        name: template.name,
        description: template.description,
        method: template.method,
        url: template.url,
        webhookConfig: {
          ...formData.webhookConfig,
          ...(template.webhookConfig || {}),
          headers: (template.webhookConfig as any)?.headers || [],
          pathParameters: (template.webhookConfig as any)?.pathParameters || [],
          queryParameters: (template.webhookConfig as any)?.queryParameters || [],
          bodyParameters: (template.webhookConfig as any)?.bodyParameters || [],
        }
      });
    }
  };

  useEffect(() => {
    if (webhook) {
      setFormData({
        ...webhook,
        webhookConfig: {
          ...webhook.webhookConfig,
          responseTimeout: webhook.webhookConfig?.responseTimeout || 20,
          disableInterruptions: webhook.webhookConfig?.disableInterruptions || false,
          preToolSpeech: webhook.webhookConfig?.preToolSpeech || 'auto',
          headers: webhook.webhookConfig?.headers || [],
          pathParameters: webhook.webhookConfig?.pathParameters || [],
          queryParameters: webhook.webhookConfig?.queryParameters || [],
          bodyParameters: webhook.webhookConfig?.bodyParameters || [],
          dynamicVariables: webhook.webhookConfig?.dynamicVariables || {},
          dynamicVariableAssignments: webhook.webhookConfig?.dynamicVariableAssignments || [],
        },
      });
    }
  }, [webhook]);

  const handleSave = () => {
    if (showJsonEditor) {
      try {
        const parsed = JSON.parse(jsonContent);
        onSave(parsed);
      } catch (error) {
        return;
      }
    } else {
      onSave(formData);
    }
    onClose();
  };

  const addHeader = () => {
    setFormData({
      ...formData,
      webhookConfig: {
        ...formData.webhookConfig,
        headers: [
          ...(formData.webhookConfig?.headers || []),
          { key: '', value: '', enabled: true }
        ],
      },
    });
  };

  const updateHeader = (index: number, field: 'key' | 'value' | 'enabled', value: any) => {
    const headers = [...(formData.webhookConfig?.headers || [])];
    headers[index] = { ...headers[index], [field]: value };
    setFormData({
      ...formData,
      webhookConfig: { ...formData.webhookConfig, headers },
    });
  };

  const deleteHeader = (index: number) => {
    const headers = [...(formData.webhookConfig?.headers || [])];
    headers.splice(index, 1);
    setFormData({
      ...formData,
      webhookConfig: { ...formData.webhookConfig, headers },
    });
  };

  const addQueryParameter = () => {
    setFormData({
      ...formData,
      webhookConfig: {
        ...formData.webhookConfig,
        queryParameters: [
          ...(formData.webhookConfig?.queryParameters || []),
          { identifier: '', description: '', required: false, dataType: 'String', valueType: 'LLM Prompt' }
        ],
      },
    });
  };

  const addBodyParameter = () => {
    setFormData({
      ...formData,
      webhookConfig: {
        ...formData.webhookConfig,
        bodyParameters: [
          ...(formData.webhookConfig?.bodyParameters || []),
          { 
            identifier: '', 
            dataType: 'String' as const, 
            description: '', 
            required: false,
            valueType: 'LLM Prompt' as const
          }
        ],
      },
    });
  };

  const updateQueryParameter = (index: number, field: 'identifier' | 'description' | 'required' | 'dataType' | 'valueType', value: any) => {
    const params = [...(formData.webhookConfig?.queryParameters || [])];
    params[index] = { ...params[index], [field]: value };
    setFormData({
      ...formData,
      webhookConfig: { ...formData.webhookConfig, queryParameters: params },
    });
  };

  const deleteQueryParameter = (index: number) => {
    const params = [...(formData.webhookConfig?.queryParameters || [])];
    params.splice(index, 1);
    setFormData({
      ...formData,
      webhookConfig: { ...formData.webhookConfig, queryParameters: params },
    });
  };

  const updateBodyParameter = (index: number, field: string, value: any) => {
    const updatedParams = [...(formData.webhookConfig?.bodyParameters || [])];
    updatedParams[index] = { ...updatedParams[index], [field]: value };
    setFormData({
      ...formData,
      webhookConfig: {
        ...formData.webhookConfig,
        bodyParameters: updatedParams,
      },
    });
  };

  const deleteBodyParameter = (index: number) => {
    setFormData({
      ...formData,
      webhookConfig: {
        ...formData.webhookConfig,
        bodyParameters: formData.webhookConfig?.bodyParameters?.filter((_, i) => i !== index) || [],
      },
    });
  };

  const addDynamicVariableAssignment = () => {
    setFormData({
      ...formData,
      webhookConfig: {
        ...formData.webhookConfig,
        dynamicVariableAssignments: [
          ...(formData.webhookConfig?.dynamicVariableAssignments || []),
          { variable: '', jsonPath: '' }
        ],
      },
    });
  };

  const updateDynamicVariableAssignment = (index: number, field: 'variable' | 'jsonPath', value: string) => {
    const assignments = [...(formData.webhookConfig?.dynamicVariableAssignments || [])];
    assignments[index] = { ...assignments[index], [field]: value };
    setFormData({
      ...formData,
      webhookConfig: { ...formData.webhookConfig, dynamicVariableAssignments: assignments },
    });
  };

  const deleteDynamicVariableAssignment = (index: number) => {
    const assignments = [...(formData.webhookConfig?.dynamicVariableAssignments || [])];
    assignments.splice(index, 1);
    setFormData({
      ...formData,
      webhookConfig: { ...formData.webhookConfig, dynamicVariableAssignments: assignments },
    });
  };

  const toggleJsonEditor = () => {
    if (!showJsonEditor) {
      setJsonContent(JSON.stringify(formData, null, 2));
    } else {
      try {
        const parsed = JSON.parse(jsonContent);
        setFormData(parsed);
      } catch (error) {
        return;
      }
    }
    setShowJsonEditor(!showJsonEditor);
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <span>Add webhook tool</span>
          </DialogTitle>
        </DialogHeader>

        {showJsonEditor ? (
          <div className="space-y-4">
            <Textarea
              value={jsonContent}
              onChange={(e) => setJsonContent(e.target.value)}
              className="font-mono text-sm min-h-[400px]"
              placeholder="Enter JSON configuration..."
            />
          </div>
        ) : (
          <div className="space-y-6">
            {/* Template Selection */}
            {!webhook && (
              <Alert className="border-blue-200 dark:border-blue-800 bg-blue-50 dark:bg-blue-900/20">
                <Sparkles className="h-4 w-4 text-blue-600 dark:text-blue-400" />
                <AlertDescription className="flex flex-col gap-3">
                  <div className="text-sm font-medium text-blue-900 dark:text-blue-100">
                    Quick Start Templates
                  </div>
                  <div className="flex flex-wrap gap-2">
                    <Button
                      type="button"
                      variant="outline"
                      size="sm"
                      onClick={() => {
                        setSelectedTemplate('custom');
                        applyTemplate('custom');
                      }}
                      className={selectedTemplate === 'custom' ? 'border-blue-500 bg-blue-50 dark:bg-blue-900/50' : ''}
                    >
                      Custom Webhook
                    </Button>
                  </div>
                </AlertDescription>
              </Alert>
            )}

            {/* Configuration Section */}
            <div className="space-y-4">
              <div>
                <h3 className="text-sm font-semibold mb-1">Configuration</h3>
                <p className="text-xs text-muted-foreground">Describe to the LLM how and when to use the tool.</p>
              </div>

              <div>
                <Label htmlFor="name">Name</Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  placeholder="Enter webhook name"
                  className="mt-1"
                />
              </div>

              <div>
                <Label htmlFor="description">Description</Label>
                <Textarea
                  id="description"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  placeholder="Describe what this webhook does..."
                  className="mt-1 min-h-[80px]"
                />
              </div>

              <div className="grid grid-cols-[120px_1fr] gap-4">
                <div>
                  <Label htmlFor="method">Method</Label>
                  <Select
                    value={formData.method}
                    onValueChange={(value: any) => setFormData({ ...formData, method: value })}
                  >
                    <SelectTrigger id="method" className="mt-1">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="GET">GET</SelectItem>
                      <SelectItem value="POST">POST</SelectItem>
                      <SelectItem value="PUT">PUT</SelectItem>
                      <SelectItem value="PATCH">PATCH</SelectItem>
                      <SelectItem value="DELETE">DELETE</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="url">URL</Label>
                  <Input
                    id="url"
                    value={formData.url}
                    onChange={(e) => setFormData({ ...formData, url: e.target.value })}
                    placeholder="https://api.example.com/endpoint"
                    className="mt-1"
                  />
                </div>
              </div>

              <div>
                <div className="flex items-center justify-between mb-2">
                  <Label>Response timeout (seconds)</Label>
                  <span className="text-sm text-muted-foreground">
                    {formData.webhookConfig?.responseTimeout || 20}s
                  </span>
                </div>
                <p className="text-xs text-muted-foreground mb-2">
                  How long to wait for the client tool to respond before timing out. Default is 20 seconds.
                </p>
                <Slider
                  value={[formData.webhookConfig?.responseTimeout || 20]}
                  onValueChange={(value) => setFormData({
                    ...formData,
                    webhookConfig: { ...formData.webhookConfig, responseTimeout: value[0] }
                  })}
                  min={1}
                  max={60}
                  step={1}
                  className="w-full"
                />
              </div>

              <div className="flex items-center space-x-2">
                <Switch
                  id="disable-interruptions"
                  checked={formData.webhookConfig?.disableInterruptions || false}
                  onCheckedChange={(checked) => setFormData({
                    ...formData,
                    webhookConfig: { ...formData.webhookConfig, disableInterruptions: checked }
                  })}
                />
                <div>
                  <Label htmlFor="disable-interruptions" className="text-sm font-medium">
                    Disable interruptions
                  </Label>
                  <p className="text-xs text-muted-foreground">
                    Select this box to disable interruptions while the tool is running.
                  </p>
                </div>
              </div>

              <div>
                <Label htmlFor="pre-tool-speech">Pre-tool speech</Label>
                <p className="text-xs text-muted-foreground mb-2">
                  Force agent speech before tool execution or let it decide automatically based on recent execution times.
                </p>
                <Select
                  value={formData.webhookConfig?.preToolSpeech || 'auto'}
                  onValueChange={(value: any) => setFormData({
                    ...formData,
                    webhookConfig: { ...formData.webhookConfig, preToolSpeech: value }
                  })}
                >
                  <SelectTrigger id="pre-tool-speech" className="mt-1">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="auto">Auto</SelectItem>
                    <SelectItem value="force">Force</SelectItem>
                    <SelectItem value="none">None</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label>Authentication</Label>
                <div className="mt-1 p-3 border rounded-lg text-sm text-muted-foreground">
                  Workspace has no auth connections
                </div>
              </div>
            </div>

            {/* Headers Section */}
            <div>
              <div className="flex items-center justify-between mb-2">
                <div>
                  <h3 className="text-sm font-semibold">Headers</h3>
                  <p className="text-xs text-muted-foreground">Define headers that will be sent with the request</p>
                </div>
                <Button onClick={addHeader} size="sm" variant="outline">
                  Add header
                </Button>
              </div>
              {formData.webhookConfig?.headers?.map((header, index) => (
                <div key={index} className="flex gap-2 items-center mt-2">
                  <Input
                    placeholder="Key"
                    value={header.key}
                    onChange={(e) => updateHeader(index, 'key', e.target.value)}
                    className="flex-1"
                  />
                  <Input
                    placeholder="Value"
                    value={header.value}
                    onChange={(e) => updateHeader(index, 'value', e.target.value)}
                    className="flex-1"
                  />
                  <Switch
                    checked={header.enabled}
                    onCheckedChange={(checked) => updateHeader(index, 'enabled', checked)}
                  />
                  <Button
                    onClick={() => deleteHeader(index)}
                    size="icon"
                    variant="ghost"
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              ))}
            </div>

            {/* Path Parameters Section */}
            <div>
              <h3 className="text-sm font-semibold mb-1">Path parameters</h3>
              <p className="text-xs text-muted-foreground">
                Add path wrapped in curly braces to the URL to configure them here.
              </p>
            </div>

            {/* Query Parameters Section */}
            <div>
              <div className="flex items-center justify-between mb-2">
                <div>
                  <h3 className="text-sm font-semibold">Query parameters</h3>
                  <p className="text-xs text-muted-foreground">
                    Define parameters that will be collected by the LLM and sent as the query of the request.
                  </p>
                </div>
                <Button onClick={addQueryParameter} size="sm" variant="outline">
                  Add param
                </Button>
              </div>
              {formData.webhookConfig?.queryParameters?.map((param, index) => (
                <div key={index} className="border rounded-lg p-3 mt-2 space-y-2 bg-muted/20">
                  <div className="grid grid-cols-2 gap-2">
                    <div>
                      <Label className="text-xs">Identifier</Label>
                      <Input
                        placeholder="e.g., searchQuery"
                        value={param.identifier || (param as any).key}
                        onChange={(e) => updateQueryParameter(index, 'identifier', e.target.value)}
                        className="mt-1"
                      />
                    </div>
                    <div>
                      <Label className="text-xs">Data Type</Label>
                      <Select
                        value={param.dataType || 'String'}
                        onValueChange={(value) => updateQueryParameter(index, 'dataType', value)}
                      >
                        <SelectTrigger className="mt-1">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="String">String</SelectItem>
                          <SelectItem value="Number">Number</SelectItem>
                          <SelectItem value="Boolean">Boolean</SelectItem>
                          <SelectItem value="Object">Object</SelectItem>
                          <SelectItem value="Array">Array</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                  </div>
                  <div>
                    <Label className="text-xs">Description</Label>
                    <Input
                      placeholder="Describe how to extract this parameter"
                      value={param.description}
                      onChange={(e) => updateQueryParameter(index, 'description', e.target.value)}
                      className="mt-1"
                    />
                  </div>
                  <div className="grid grid-cols-2 gap-2">
                    <div>
                      <Label className="text-xs">Value Type</Label>
                      <Select
                        value={param.valueType || 'LLM Prompt'}
                        onValueChange={(value) => updateQueryParameter(index, 'valueType', value)}
                      >
                        <SelectTrigger className="mt-1">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="LLM Prompt">LLM Prompt</SelectItem>
                          <SelectItem value="Static">Static</SelectItem>
                          <SelectItem value="Dynamic Variable">Dynamic Variable</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                    <div className="flex items-center gap-2 mt-6">
                      <Switch
                        checked={param.required}
                        onCheckedChange={(checked) => updateQueryParameter(index, 'required', checked)}
                      />
                      <Label className="text-xs">Required</Label>
                    </div>
                  </div>
                  <div className="flex justify-end">
                    <Button
                      onClick={() => deleteQueryParameter(index)}
                      size="sm"
                      variant="ghost"
                      className="text-destructive"
                    >
                      Delete
                    </Button>
                  </div>
                </div>
              ))}
            </div>

            {/* Body Parameters Section */}
            {(formData.method === 'POST' || formData.method === 'PUT' || formData.method === 'PATCH') && (
              <div>
                <div className="flex items-center justify-between mb-2">
                  <div>
                    <h3 className="text-sm font-semibold">Body parameters</h3>
                    <p className="text-xs text-muted-foreground">
                      Define parameters that will be collected by the LLM and sent as the body of the request.
                    </p>
                  </div>
                  <Button onClick={addBodyParameter} size="sm" variant="outline">
                    Add property
                  </Button>
                </div>
                {formData.webhookConfig?.bodyParameters?.map((param, index) => (
                  <div key={index} className="border rounded-lg p-4 mt-3 space-y-3 bg-muted/20">
                    <div>
                      <Label className="text-xs">Description</Label>
                      <Textarea
                        placeholder="Extract the search query the user in looking to find more information on"
                        value={param.description}
                        onChange={(e) => updateBodyParameter(index, 'description', e.target.value)}
                        className="mt-1 min-h-[60px]"
                      />
                      <p className="text-xs text-muted-foreground mt-1">
                        This field will be passed to the LLM and should describe in detail how to extract the data from the transcript.
                      </p>
                    </div>

                    <div>
                      <Label className="text-xs font-semibold">Properties</Label>
                      <div className="grid grid-cols-2 gap-3 mt-2">
                        <div>
                          <Label className="text-xs">Data type</Label>
                          <Select
                            value={param.dataType}
                            onValueChange={(value) => updateBodyParameter(index, 'dataType', value)}
                          >
                            <SelectTrigger className="mt-1">
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="String">String</SelectItem>
                              <SelectItem value="Number">Number</SelectItem>
                              <SelectItem value="Boolean">Boolean</SelectItem>
                              <SelectItem value="Object">Object</SelectItem>
                              <SelectItem value="Array">Array</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>
                        <div>
                          <Label className="text-xs">Identifier</Label>
                          <Input
                            placeholder="searchQuery"
                            value={param.identifier}
                            onChange={(e) => updateBodyParameter(index, 'identifier', e.target.value)}
                            className="mt-1"
                          />
                        </div>
                      </div>
                      <div className="flex items-center gap-2 mt-3">
                        <Switch
                          checked={param.required}
                          onCheckedChange={(checked) => updateBodyParameter(index, 'required', checked)}
                        />
                        <Label className="text-xs">Required</Label>
                      </div>
                      <div className="mt-3">
                        <Label className="text-xs">Value Type</Label>
                        <Select
                          value={param.valueType}
                          onValueChange={(value) => updateBodyParameter(index, 'valueType', value)}
                        >
                          <SelectTrigger className="mt-1">
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="LLM Prompt">LLM Prompt</SelectItem>
                            <SelectItem value="Static">Static</SelectItem>
                            <SelectItem value="Dynamic Variable">Dynamic Variable</SelectItem>
                          </SelectContent>
                        </Select>
                        <p className="text-xs text-muted-foreground mt-1">
                          How the value will be determined (e.g., LLM will extract it from conversation)
                        </p>
                      </div>
                    </div>

                    <div className="flex justify-end">
                      <Button
                        onClick={() => deleteBodyParameter(index)}
                        size="sm"
                        variant="ghost"
                        className="text-destructive"
                      >
                        Delete
                      </Button>
                    </div>
                  </div>
                ))}              </div>
            )}

            {/* Dynamic Variables Section */}
            <div>
              <h3 className="text-sm font-semibold mb-1">Dynamic Variables</h3>
              <p className="text-xs text-muted-foreground">
                Variables in tool parameters will be replaced with actual values when the conversation starts.{' '}
                <a href="#" className="text-primary hover:underline">Learn more</a>
              </p>
            </div>

            {/* Dynamic Variable Assignments Section */}
            <div>
              <div className="flex items-center justify-between mb-2">
                <div>
                  <h3 className="text-sm font-semibold">Dynamic Variable Assignments</h3>
                  <p className="text-xs text-muted-foreground">
                    Configure which dynamic variables can be updated when this tool returns a response.{' '}
                    <a href="#" className="text-primary hover:underline">Learn more</a>
                  </p>
                </div>
                <Button onClick={addDynamicVariableAssignment} size="sm" variant="outline">
                  Add assignment
                </Button>
              </div>
              {formData.webhookConfig?.dynamicVariableAssignments?.map((assignment, index) => (
                <div key={index} className="flex gap-2 items-center mt-2">
                  <Input
                    placeholder="Variable name"
                    value={assignment.variable}
                    onChange={(e) => updateDynamicVariableAssignment(index, 'variable', e.target.value)}
                    className="flex-1"
                  />
                  <Input
                    placeholder="JSON path"
                    value={assignment.jsonPath}
                    onChange={(e) => updateDynamicVariableAssignment(index, 'jsonPath', e.target.value)}
                    className="flex-1"
                  />
                  <Button
                    onClick={() => deleteDynamicVariableAssignment(index)}
                    size="icon"
                    variant="ghost"
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              ))}
            </div>
          </div>
        )}

        <DialogFooter className="flex items-center justify-between">
          <Button
            variant="ghost"
            onClick={toggleJsonEditor}
            className="flex items-center gap-2"
          >
            <Code className="w-4 h-4" />
            {showJsonEditor ? 'Edit as form' : 'Edit as JSON'}
          </Button>
          <div className="flex gap-2">
            <Button variant="outline" onClick={onClose}>
              Cancel
            </Button>
            <Button onClick={handleSave}>
              Add tool
            </Button>
          </div>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

