/**
 * Storage Adapter Interface
 * 
 * Platform-agnostic storage abstraction for audio files and uploads.
 * Supports local filesystem, AWS S3, Google Cloud Storage, and Azure Blob Storage.
 */

export interface StorageMetadata {
  contentType?: string;
  cacheControl?: string;
  metadata?: Record<string, string>;
}

export interface StorageAdapter {
  /**
   * Save a file to storage
   * @param key - Unique identifier for the file
   * @param buffer - File content as Buffer
   * @param metadata - Optional metadata for the file
   * @returns The storage key (path or URL)
   */
  save(key: string, buffer: Buffer, metadata?: StorageMetadata): Promise<string>;

  /**
   * Retrieve a file from storage
   * @param key - Unique identifier for the file
   * @returns File content as Buffer
   */
  get(key: string): Promise<Buffer>;

  /**
   * Delete a file from storage
   * @param key - Unique identifier for the file
   */
  delete(key: string): Promise<void>;

  /**
   * Check if a file exists in storage
   * @param key - Unique identifier for the file
   * @returns True if file exists
   */
  exists(key: string): Promise<boolean>;

  /**
   * Get a public URL for a file (if supported)
   * @param key - Unique identifier for the file
   * @returns Public URL or null if not supported
   */
  getPublicUrl(key: string): string | null;

  /**
   * Get a signed/temporary URL for a file (if supported)
   * @param key - Unique identifier for the file
   * @param expiresIn - Expiration time in seconds
   * @returns Signed URL or null if not supported
   */
  getSignedUrl(key: string, expiresIn?: number): Promise<string | null>;
}

server/config.ts.content:
import { config as loadEnv } from 'dotenv';

// Load environment variables from .env file
loadEnv();

interface Config {
  // Environment
  nodeEnv: string;
  isDevelopment: boolean;
  isProduction: boolean;

  // Server
  host: string;
  port: number;
  publicUrl: string;
  baseDomain: string | null;

  // Database
  database: {
    url: string;
    ssl: boolean;
    maxConnections: number;
  };

  // Security & Auth
  security: {
    sessionSecret: string;
    encryptionKey: string;
    trustProxy: boolean;
  };

  // External Services
  integrations: {
    elevenlabs: {
      apiKey: string | null;
    };
    stripe: {
      secretKey: string | null;
      webhookSecret: string | null;
    };
    sendgrid: {
      apiKey: string | null;
    };
    google: {
      projectId: string | null;
      credentialsPath: string | null;
    };
  };

  // Storage
  storage: {
    provider: 'local' | 's3' | 'gcs' | 'azure';
    local: {
      uploadDir: string;
      audioDir: string;
    };
    s3?: {
      bucket: string;
      region: string;
      accessKeyId: string;
      secretAccessKey: string;
    };
    gcs?: {
      bucket: string;
      projectId: string;
      keyFilePath: string;
    };
    azure?: {
      accountName: string;
      accountKey: string;
      containerName: string;
    };
  };
}

/**
 * Validate and load application configuration from environment variables
 */
function loadConfig(): Config {
  const nodeEnv = process.env.NODE_ENV || 'development';
  const isDevelopment = nodeEnv === 'development';
  const isProduction = nodeEnv === 'production';

  // Required secrets - must be provided (no defaults for security)
  const requiredSecrets = {
    sessionSecret: process.env.SESSION_SECRET,
    encryptionKey: process.env.ENCRYPTION_KEY,
  };

  // Validate required secrets in production
  if (isProduction) {
    const missingSecrets: string[] = [];

    if (!requiredSecrets.sessionSecret || requiredSecrets.sessionSecret.length < 32) {
      missingSecrets.push('SESSION_SECRET (must be at least 32 characters)');
    }

    if (!requiredSecrets.encryptionKey || requiredSecrets.encryptionKey.length < 32) {
      missingSecrets.push('ENCRYPTION_KEY (must be at least 32 characters)');
    }

    if (missingSecrets.length > 0) {
      throw new Error(
        `Missing or invalid required secrets in production:\n${missingSecrets.map(s => `  - ${s}`).join('\n')}\n\n` +
        'Please set these environment variables before starting the application.'
      );
    }
  }

  // Use secure defaults for development, but warn
  const sessionSecret = requiredSecrets.sessionSecret || (() => {
    if (isDevelopment) {
      // Use console.warn here as logger may not be initialized yet
      console.warn('[CONFIG] Using insecure default SESSION_SECRET in development');
      return 'dev-session-secret-change-in-production-minimum-32-chars';
    }
    throw new Error('SESSION_SECRET is required');
  })();

  const encryptionKey = requiredSecrets.encryptionKey || (() => {
    if (isDevelopment) {
      console.warn('[CONFIG] Using insecure default ENCRYPTION_KEY in development');
      return 'dev-encryption-key-change-in-production-min-32-chars';
    }
    throw new Error('ENCRYPTION_KEY is required');
  })();

  // Server configuration
  const host = process.env.HOST || '0.0.0.0';
  const port = parseInt(process.env.PORT || '5000', 10);

  // Public URL for external links and webhooks
  const publicUrl = process.env.PUBLIC_URL || 
    (isDevelopment ? `http://localhost:${port}` : `http://${host}:${port}`);

  const baseDomain = process.env.BASE_DOMAIN || null;

  // Database configuration
  const databaseUrl = process.env.DATABASE_URL;
  if (!databaseUrl) {
    throw new Error('DATABASE_URL is required');
  }

  const database = {
    url: databaseUrl,
    ssl: process.env.DATABASE_SSL === 'true' || isProduction,
    maxConnections: parseInt(process.env.DATABASE_MAX_CONNECTIONS || '20', 10),
  };

  // Security configuration
  const security = {
    sessionSecret,
    encryptionKey,
    trustProxy: process.env.TRUST_PROXY === 'true' || isProduction,
  };

  // External integrations (optional, can be configured later)
  const integrations = {
    elevenlabs: {
      apiKey: process.env.ELEVENLABS_API_KEY || null,
    },
    stripe: {
      secretKey: process.env.STRIPE_SECRET_KEY || null,
      webhookSecret: process.env.STRIPE_WEBHOOK_SECRET || null,
    },
    sendgrid: {
      apiKey: process.env.SENDGRID_API_KEY || null,
    },
    google: {
      projectId: process.env.GOOGLE_CLOUD_PROJECT || null,
      credentialsPath: process.env.GOOGLE_APPLICATION_CREDENTIALS || null,
    },
  };

  // Storage configuration
  const storageProvider = (process.env.STORAGE_PROVIDER || 'local') as Config['storage']['provider'];

  const storage: Config['storage'] = {
    provider: storageProvider,
    local: {
      uploadDir: process.env.UPLOAD_DIR || 'uploads',
      audioDir: process.env.AUDIO_DIR || 'audio-storage',
    },
  };

  // S3 storage configuration
  if (storageProvider === 's3') {
    const s3Bucket = process.env.S3_BUCKET;
    const s3Region = process.env.S3_REGION || 'us-east-1';
    const s3AccessKeyId = process.env.S3_ACCESS_KEY_ID;
    const s3SecretAccessKey = process.env.S3_SECRET_ACCESS_KEY;

    if (!s3Bucket || !s3AccessKeyId || !s3SecretAccessKey) {
      throw new Error(
        'S3 storage requires: S3_BUCKET, S3_ACCESS_KEY_ID, S3_SECRET_ACCESS_KEY'
      );
    }

    storage.s3 = {
      bucket: s3Bucket,
      region: s3Region,
      accessKeyId: s3AccessKeyId,
      secretAccessKey: s3SecretAccessKey,
    };
  }

  // Google Cloud Storage configuration
  if (storageProvider === 'gcs') {
    const gcsBucket = process.env.GCS_BUCKET;
    const gcsProjectId = process.env.GCS_PROJECT_ID;
    const gcsKeyFilePath = process.env.GCS_KEY_FILE_PATH;

    if (!gcsBucket || !gcsProjectId || !gcsKeyFilePath) {
      throw new Error(
        'GCS storage requires: GCS_BUCKET, GCS_PROJECT_ID, GCS_KEY_FILE_PATH'
      );
    }

    storage.gcs = {
      bucket: gcsBucket,
      projectId: gcsProjectId,
      keyFilePath: gcsKeyFilePath,
    };
  }

  // Azure Blob Storage configuration
  if (storageProvider === 'azure') {
    const azureAccountName = process.env.AZURE_STORAGE_ACCOUNT_NAME;
    const azureAccountKey = process.env.AZURE_STORAGE_ACCOUNT_KEY;
    const azureContainerName = process.env.AZURE_STORAGE_CONTAINER_NAME;

    if (!azureAccountName || !azureAccountKey || !azureContainerName) {
      throw new Error(
        'Azure storage requires: AZURE_STORAGE_ACCOUNT_NAME, AZURE_STORAGE_ACCOUNT_KEY, AZURE_STORAGE_CONTAINER_NAME'
      );
    }

    storage.azure = {
      accountName: azureAccountName,
      accountKey: azureAccountKey,
      containerName: azureContainerName,
    };
  }

  // Log configuration status (without sensitive values)
  console.log('[CONFIG] Environment:', nodeEnv);
  console.log('[CONFIG] Server:', `${host}:${port}`);
  console.log('[CONFIG] Public URL:', publicUrl);
  console.log('[CONFIG] Database:', databaseUrl.split('@')[1] || 'configured');
  console.log('[CONFIG] Storage provider:', storageProvider);

  if (integrations.elevenlabs.apiKey) {
    console.log('[CONFIG]  ElevenLabs API key configured');
  }
  if (integrations.stripe.secretKey) {
    console.log('[CONFIG]  Stripe configured');
  }

  return {
    nodeEnv,
    isDevelopment,
    isProduction,
    host,
    port,
    publicUrl,
    baseDomain,
    database,
    security,
    integrations,
    storage,
  };
}

// Export singleton configuration
export const config = loadConfig();

// Export type for use in other modules
export type AppConfig = typeof config;

server/services/providers/types.ts.content:
import { Readable } from 'stream';

export type ProviderType = 'tts' | 'stt' | 'llm' | 'telephony' | 'conversational_ai' | 'vad' | 'knowledge_base';

export interface IProvider {
    id: string;
    name: string;
    type: ProviderType;
    initialize(config: any): Promise<void>;
    healthCheck?(): Promise<boolean>;
}

// 1. Text-to-Speech (TTS) Provider
export interface ITTSProvider extends IProvider {
    getVoices(): Promise<any[]>;
    getVoice(voiceId: string): Promise<any>;
    generateAudio(text: string, voiceId: string, options?: any): Promise<ArrayBuffer | Buffer>;
    streamAudio?(text: string, voiceId: string, options?: any): Promise<ReadableStream | Readable>;
}

// 2. Speech-to-Text (STT) Provider (NEW)
export interface ISTTProvider extends IProvider {
    transcribe(audioBuffer: Buffer, options?: any): Promise<string>;
    createTranscriptionStream?(): any; // Return a writable stream that emits transcription events
}

// 3. Large Language Model (LLM) Provider
export interface ILLMProvider extends IProvider {
    generateResponse(prompt: string, context?: any[], options?: any): Promise<string>;
    streamResponse(prompt: string, context?: any[], options?: any): Promise<ReadableStream | Readable>;
}

// 4. Voice Activity Detection (VAD) Provider (NEW)
export interface IVADProvider extends IProvider {
    detectVoice(audioChunk: Buffer): Promise<{ isSpeech: boolean; confidence: number }>;
}

// 5. Telephony Provider
export interface ITelephonyProvider extends IProvider {
    getPhoneNumbers(agentId?: string): Promise<any[]>;
    createPhoneNumber(data: any): Promise<any>;
    deletePhoneNumber(phoneNumberId: string): Promise<any>;
    makeOutboundCall(to: string, from: string, config: any): Promise<any>;
}

// 6. Conversational AI Provider (All-in-One wrappers like ElevenLabs/Vapi)
export interface IConversationalAIProvider extends IProvider {
    // Agent Management
    createAgent(agentData: any): Promise<any>;
    updateAgent(agentId: string, updates: any): Promise<any>;
    deleteAgent(agentId: string): Promise<any>;
    getAgent(agentId: string): Promise<any>;
    getAgents(): Promise<any[]>;

    // Conversation Management
    getConversations(params?: any): Promise<any>;
    getConversation(conversationId: string): Promise<any>;
    getConversationTranscript(conversationId: string): Promise<any>;
    getConversationAudio(conversationId: string): Promise<{ buffer: Buffer | null; error?: string; notFound?: boolean }>;

    // WebRTC / Realtime
    createWebRTCSession(agentId: string, options?: any): Promise<any>;

    // Tools
    manageTools(tools: any[], integrationId?: string): Promise<{ toolIds: string[]; builtInTools: any }>;

    // Batch Calling
    createBatchCall(data: any): Promise<any>;
    getBatchCalls(): Promise<any[]>;
    getBatchCallStatus(batchId: string): Promise<any>;
    cancelBatchCall(batchId: string): Promise<any>;

    // Conversation Initiation
    createConversation(data: any): Promise<any>;
}

server/utils/logger.ts.content:
/**
 * Centralized logging utility for EchoSenseiX
 * Provides structured logging with different levels and automatic sanitization
 */

import { config } from '../config';

// Log levels
export enum LogLevel {
  ERROR = 'error',
  WARN = 'warn',
  INFO = 'info',
  DEBUG = 'debug',
}

// Log entry interface
interface LogEntry {
  level: LogLevel;
  message: string;
  timestamp: string;
  context?: Record<string, any>;
  error?: Error;
}

// Sensitive keys that should be redacted in logs
const SENSITIVE_KEYS = [
  'password',
  'apikey',
  'api_key',
  'token',
  'secret',
  'authorization',
  'cookie',
  'session',
  'credentials',
  'encryptionkey',
  'encryption_key',
];

/**
 * Sanitizes sensitive data from log context
 */
function sanitizeContext(context: any): any {
  if (!context || typeof context !== 'object') {
    return context;
  }

  if (Array.isArray(context)) {
    return context.map(sanitizeContext);
  }

  const sanitized: Record<string, any> = {};

  for (const [key, value] of Object.entries(context)) {
    const lowerKey = key.toLowerCase();

    // Check if key contains sensitive information
    const isSensitive = SENSITIVE_KEYS.some((sensitiveKey) =>
      lowerKey.includes(sensitiveKey)
    );

    if (isSensitive) {
      // Redact sensitive values but show last 4 characters if it's a string
      if (typeof value === 'string' && value.length > 4) {
        sanitized[key] = `***${value.slice(-4)}`;
      } else {
        sanitized[key] = '***REDACTED***';
      }
    } else if (typeof value === 'object' && value !== null) {
      sanitized[key] = sanitizeContext(value);
    } else {
      sanitized[key] = value;
    }
  }

  return sanitized;
}

/**
 * Formats log entry for console output
 */
function formatLogEntry(entry: LogEntry): string {
  const { level, message, timestamp, context, error } = entry;

  let output = `[${timestamp}] [${level.toUpperCase()}] ${message}`;

  if (context && Object.keys(context).length > 0) {
    output += ` ${JSON.stringify(context)}`;
  }

  if (error) {
    output += `\n  Error: ${error.message}`;
    if (config.isDevelopment && error.stack) {
      output += `\n  Stack: ${error.stack}`;
    }
  }

  return output;
}

/**
 * Gets color code for log level (for terminal output)
 */
function getColorCode(level: LogLevel): string {
  const colors = {
    [LogLevel.ERROR]: '\x1b[31m', // Red
    [LogLevel.WARN]: '\x1b[33m',  // Yellow
    [LogLevel.INFO]: '\x1b[36m',  // Cyan
    [LogLevel.DEBUG]: '\x1b[90m', // Gray
  };
  return colors[level] || '';
}

/**
 * Logger class with structured logging methods
 */
class Logger {
  private minLevel: LogLevel;

  constructor() {
    // Set minimum log level based on environment
    this.minLevel = config.isDevelopment ? LogLevel.DEBUG : LogLevel.INFO;
  }

  /**
   * Determines if a log level should be output
   */
  private shouldLog(level: LogLevel): boolean {
    const levels = [LogLevel.ERROR, LogLevel.WARN, LogLevel.INFO, LogLevel.DEBUG];
    const currentIndex = levels.indexOf(this.minLevel);
    const requestedIndex = levels.indexOf(level);
    return requestedIndex <= currentIndex;
  }

  /**
   * Core logging method
   */
  private log(level: LogLevel, message: string, context?: Record<string, any>, error?: Error) {
    if (!this.shouldLog(level)) {
      return;
    }

    const entry: LogEntry = {
      level,
      message,
      timestamp: new Date().toISOString(),
      context: context ? sanitizeContext(context) : undefined,
      error,
    };

    const formattedMessage = formatLogEntry(entry);
    const colorCode = getColorCode(level);
    const resetCode = '\x1b[0m';

    // Output to console with color in development
    if (config.isDevelopment) {
      console.log(`${colorCode}${formattedMessage}${resetCode}`);
    } else {
      // In production, use standard console methods
      switch (level) {
        case LogLevel.ERROR:
          console.error(formattedMessage);
          break;
        case LogLevel.WARN:
          console.warn(formattedMessage);
          break;
        default:
          console.log(formattedMessage);
      }
    }

    // TODO: In production, also send to external logging service (e.g., CloudWatch, Datadog)
    // if (config.isProduction) {
    //   sendToExternalLogger(entry);
    // }
  }

  /**
   * Log error message
   */
  error(message: string, context?: Record<string, any>, error?: Error) {
    this.log(LogLevel.ERROR, message, context, error);
  }

  /**
   * Log warning message
   */
  warn(message: string, context?: Record<string, any>) {
    this.log(LogLevel.WARN, message, context);
  }

  /**
   * Log info message
   */
  info(message: string, context?: Record<string, any>) {
    this.log(LogLevel.INFO, message, context);
  }

  /**
   * Log debug message (only in development)
   */
  debug(message: string, context?: Record<string, any>) {
    this.log(LogLevel.DEBUG, message, context);
  }

  /**
   * Log HTTP request
   */
  http(method: string, path: string, statusCode: number, duration: number, context?: Record<string, any>) {
    const message = `${method} ${path} ${statusCode} in ${duration}ms`;

    if (statusCode >= 500) {
      this.error(message, context);
    } else if (statusCode >= 400) {
      this.warn(message, context);
    } else {
      this.info(message, context);
    }
  }

  /**
   * Log database query
   */
  query(operation: string, duration: number, context?: Record<string, any>) {
    const message = `DB ${operation} completed in ${duration}ms`;

    if (duration > 1000) {
      this.warn(`Slow query: ${message}`, context);
    } else {
      this.debug(message, context);
    }
  }

  /**
   * Log external API call
   */
  external(service: string, operation: string, success: boolean, duration: number, context?: Record<string, any>) {
    const message = `${service} ${operation} ${success ? 'succeeded' : 'failed'} in ${duration}ms`;

    if (!success) {
      this.error(message, context);
    } else if (duration > 5000) {
      this.warn(`Slow external call: ${message}`, context);
    } else {
      this.info(message, context);
    }
  }
}

// Export singleton instance
export const logger = new Logger();

// Export default for convenience
export default logger;

