/**
 * Email Service
 *
 * Handles sending emails for invitations, notifications, and other communication
 * Supports multiple email providers (SendGrid, Mailgun, SMTP)
 */

import nodemailer from 'nodemailer';
import { logger } from '../utils/logger';

export interface EmailOptions {
  to: string;
  subject: string;
  html: string;
  text?: string;
  from?: string;
}

export interface InvitationEmailData {
  inviteeName: string;
  inviterName: string;
  inviterCompany?: string;
  invitationCode: string;
  invitationType: 'agency' | 'user';
  customMessage?: string;
  acceptUrl: string;
}

export class EmailService {
  private static transporter: nodemailer.Transporter | null = null;

  /**
   * Initialize email transporter based on environment variables
   */
  private static getTransporter(): nodemailer.Transporter {
    if (this.transporter) {
      return this.transporter;
    }

    const emailProvider = process.env.EMAIL_PROVIDER || 'smtp';

    if (emailProvider === 'sendgrid' && process.env.SENDGRID_API_KEY) {
      // SendGrid configuration
      this.transporter = nodemailer.createTransport({
        host: 'smtp.sendgrid.net',
        port: 587,
        auth: {
          user: 'apikey',
          pass: process.env.SENDGRID_API_KEY
        }
      });
    } else if (emailProvider === 'mailgun' && process.env.MAILGUN_API_KEY) {
      // Mailgun configuration
      this.transporter = nodemailer.createTransport({
        host: process.env.MAILGUN_SMTP_HOST || 'smtp.mailgun.org',
        port: 587,
        auth: {
          user: process.env.MAILGUN_SMTP_USER,
          pass: process.env.MAILGUN_API_KEY
        }
      });
    } else if (process.env.SMTP_HOST) {
      // Generic SMTP configuration
      this.transporter = nodemailer.createTransport({
        host: process.env.SMTP_HOST,
        port: parseInt(process.env.SMTP_PORT || '587'),
        secure: process.env.SMTP_SECURE === 'true',
        auth: process.env.SMTP_USER ? {
          user: process.env.SMTP_USER,
          pass: process.env.SMTP_PASS
        } : undefined
      });
    } else {
      // Development mode: log emails to console
      logger.warn('[EMAIL] No email provider configured, emails will be logged to console');
      this.transporter = nodemailer.createTransport({
        streamTransport: true,
        newline: 'unix',
        buffer: true
      });
    }

    return this.transporter;
  }

  /**
   * Send a generic email
   */
  static async sendEmail(options: EmailOptions): Promise<boolean> {
    try {
      const transporter = this.getTransporter();
      const from = options.from || process.env.EMAIL_FROM || 'noreply@echosenseix.com';

      const mailOptions = {
        from,
        to: options.to,
        subject: options.subject,
        html: options.html,
        text: options.text || this.stripHtml(options.html)
      };

      const info = await transporter.sendMail(mailOptions);

      if (info.messageId) {
        logger.info(`[EMAIL] Email sent successfully to ${options.to}`, { messageId: info.messageId });
      } else {
        logger.info(`[EMAIL] Email logged (dev mode):`, {
          to: options.to,
          subject: options.subject,
          preview: options.html.substring(0, 200)
        });
      }

      return true;
    } catch (error: any) {
      logger.error(`[EMAIL] Failed to send email to ${options.to}:`, error);
      return false;
    }
  }

  /**
   * Send agency invitation email
   */
  static async sendAgencyInvitation(email: string, data: InvitationEmailData): Promise<boolean> {
    const subject = `You're invited to join ${data.inviterCompany || 'our platform'} as an Agency Partner`;

    const html = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .container { max-width: 600px; margin: 0 auto; padding: 20px; }
    .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; text-align: center; border-radius: 8px 8px 0 0; }
    .content { background: #f9f9f9; padding: 30px; border-radius: 0 0 8px 8px; }
    .invitation-code { background: white; padding: 15px; border-left: 4px solid #667eea; margin: 20px 0; font-family: monospace; font-size: 18px; }
    .button { display: inline-block; background: #667eea; color: white; padding: 12px 30px; text-decoration: none; border-radius: 5px; margin: 20px 0; }
    .footer { text-align: center; margin-top: 30px; color: #666; font-size: 14px; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>Agency Invitation</h1>
    </div>
    <div class="content">
      <p>Hello ${data.inviteeName},</p>

      <p><strong>${data.inviterName}</strong>${data.inviterCompany ? ` from <strong>${data.inviterCompany}</strong>` : ''} has invited you to become an Agency Partner on EchoSenseiX!</p>

      ${data.customMessage ? `<p style="background: white; padding: 15px; border-left: 4px solid #764ba2; margin: 20px 0;"><em>"${data.customMessage}"</em></p>` : ''}

      <p>As an Agency Partner, you'll be able to:</p>
      <ul>
        <li>Manage your own clients and sub-accounts</li>
        <li>Earn commissions on customer usage</li>
        <li>Access white-label features</li>
        <li>Get dedicated support and resources</li>
      </ul>

      <div class="invitation-code">
        <strong>Invitation Code:</strong> ${data.invitationCode}
      </div>

      <center>
        <a href="${data.acceptUrl}" class="button">Accept Invitation</a>
      </center>

      <p style="margin-top: 30px; font-size: 14px; color: #666;">
        Or copy and paste this link into your browser:<br>
        <a href="${data.acceptUrl}">${data.acceptUrl}</a>
      </p>
    </div>
    <div class="footer">
      <p>This invitation will expire in 7 days.</p>
      <p>&copy; ${new Date().getFullYear()} EchoSenseiX. All rights reserved.</p>
    </div>
  </div>
</body>
</html>
    `;

    return this.sendEmail({
      to: email,
      subject,
      html
    });
  }

  /**
   * Send user invitation email
   */
  static async sendUserInvitation(email: string, data: InvitationEmailData): Promise<boolean> {
    const subject = `You're invited to join ${data.inviterCompany || 'EchoSenseiX'}`;

    const html = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .container { max-width: 600px; margin: 0 auto; padding: 20px; }
    .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; text-align: center; border-radius: 8px 8px 0 0; }
    .content { background: #f9f9f9; padding: 30px; border-radius: 0 0 8px 8px; }
    .invitation-code { background: white; padding: 15px; border-left: 4px solid #667eea; margin: 20px 0; font-family: monospace; font-size: 18px; }
    .button { display: inline-block; background: #667eea; color: white; padding: 12px 30px; text-decoration: none; border-radius: 5px; margin: 20px 0; }
    .footer { text-align: center; margin-top: 30px; color: #666; font-size: 14px; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>Team Invitation</h1>
    </div>
    <div class="content">
      <p>Hello ${data.inviteeName},</p>

      <p><strong>${data.inviterName}</strong>${data.inviterCompany ? ` from <strong>${data.inviterCompany}</strong>` : ''} has invited you to join their team on EchoSenseiX!</p>

      ${data.customMessage ? `<p style="background: white; padding: 15px; border-left: 4px solid #764ba2; margin: 20px 0;"><em>"${data.customMessage}"</em></p>` : ''}

      <div class="invitation-code">
        <strong>Invitation Code:</strong> ${data.invitationCode}
      </div>

      <center>
        <a href="${data.acceptUrl}" class="button">Accept Invitation</a>
      </center>

      <p style="margin-top: 30px; font-size: 14px; color: #666;">
        Or copy and paste this link into your browser:<br>
        <a href="${data.acceptUrl}">${data.acceptUrl}</a>
      </p>
    </div>
    <div class="footer">
      <p>This invitation will expire in 7 days.</p>
      <p>&copy; ${new Date().getFullYear()} EchoSenseiX. All rights reserved.</p>
    </div>
  </div>
</body>
</html>
    `;

    return this.sendEmail({
      to: email,
      subject,
      html
    });
  }

  /**
   * Strip HTML tags from text
   */
  private static stripHtml(html: string): string {
    return html.replace(/<[^>]*>/g, '').replace(/\s+/g, ' ').trim();
  }
}

export default EmailService;

server/utils/errors.ts.content:
/**
 * Custom error classes for EchoSenseiX
 * Provides structured error handling with proper HTTP status codes
 */

/**
 * Base application error class
 * All custom errors should extend this class
 */
export class AppError extends Error {
  public readonly statusCode: number;
  public readonly isOperational: boolean;
  public readonly code?: string;
  public readonly details?: Record<string, any>;

  constructor(
    statusCode: number,
    message: string,
    isOperational = true,
    code?: string,
    details?: Record<string, any>
  ) {
    super(message);

    this.statusCode = statusCode;
    this.isOperational = isOperational;
    this.code = code;
    this.details = details;

    // Maintains proper stack trace for where error was thrown
    Object.setPrototypeOf(this, AppError.prototype);
    Error.captureStackTrace(this, this.constructor);
  }
}

/**
 * 400 Bad Request - Invalid input data
 */
export class ValidationError extends AppError {
  constructor(message: string, fields?: Record<string, string>) {
    super(400, message, true, 'VALIDATION_ERROR', { fields });
    Object.setPrototypeOf(this, ValidationError.prototype);
  }
}

/**
 * 401 Unauthorized - Authentication required
 */
export class AuthenticationError extends AppError {
  constructor(message = 'Authentication required') {
    super(401, message, true, 'AUTH_ERROR');
    Object.setPrototypeOf(this, AuthenticationError.prototype);
  }
}

/**
 * 403 Forbidden - Insufficient permissions
 */
export class AuthorizationError extends AppError {
  constructor(message = 'Insufficient permissions') {
    super(403, message, true, 'AUTHORIZATION_ERROR');
    Object.setPrototypeOf(this, AuthorizationError.prototype);
  }
}

/**
 * 404 Not Found - Resource doesn't exist
 */
export class NotFoundError extends AppError {
  constructor(resource: string, identifier?: string) {
    const message = identifier 
      ? `${resource} with identifier '${identifier}' not found`
      : `${resource} not found`;
    super(404, message, true, 'NOT_FOUND', { resource, identifier });
    Object.setPrototypeOf(this, NotFoundError.prototype);
  }
}

/**
 * 409 Conflict - Resource already exists or conflict with current state
 */
export class ConflictError extends AppError {
  constructor(message: string, details?: Record<string, any>) {
    super(409, message, true, 'CONFLICT_ERROR', details);
    Object.setPrototypeOf(this, ConflictError.prototype);
  }
}

/**
 * 422 Unprocessable Entity - Request is well-formed but semantically incorrect
 */
export class UnprocessableEntityError extends AppError {
  constructor(message: string, details?: Record<string, any>) {
    super(422, message, true, 'UNPROCESSABLE_ENTITY', details);
    Object.setPrototypeOf(this, UnprocessableEntityError.prototype);
  }
}

/**
 * 429 Too Many Requests - Rate limit exceeded
 */
export class RateLimitError extends AppError {
  constructor(message = 'Too many requests, please try again later', retryAfter?: number) {
    super(429, message, true, 'RATE_LIMIT_ERROR', { retryAfter });
    Object.setPrototypeOf(this, RateLimitError.prototype);
  }
}

/**
 * 500 Internal Server Error - Unexpected server error
 */
export class InternalServerError extends AppError {
  constructor(message = 'An unexpected error occurred', details?: Record<string, any>) {
    super(500, message, false, 'INTERNAL_ERROR', details);
    Object.setPrototypeOf(this, InternalServerError.prototype);
  }
}

/**
 * 502 Bad Gateway - External service error
 */
export class ExternalServiceError extends AppError {
  constructor(service: string, message: string, details?: Record<string, any>) {
    super(502, `${service} error: ${message}`, true, 'EXTERNAL_SERVICE_ERROR', {
      service,
      ...details,
    });
    Object.setPrototypeOf(this, ExternalServiceError.prototype);
  }
}

/**
 * 503 Service Unavailable - Service temporarily unavailable
 */
export class ServiceUnavailableError extends AppError {
  constructor(message = 'Service temporarily unavailable', retryAfter?: number) {
    super(503, message, true, 'SERVICE_UNAVAILABLE', { retryAfter });
    Object.setPrototypeOf(this, ServiceUnavailableError.prototype);
  }
}

/**
 * Database-specific errors
 */
export class DatabaseError extends AppError {
  constructor(operation: string, message: string, details?: Record<string, any>) {
    super(500, `Database ${operation} failed: ${message}`, false, 'DATABASE_ERROR', {
      operation,
      ...details,
    });
    Object.setPrototypeOf(this, DatabaseError.prototype);
  }
}

/**
 * Configuration errors
 */
export class ConfigurationError extends AppError {
  constructor(message: string, details?: Record<string, any>) {
    super(500, `Configuration error: ${message}`, false, 'CONFIG_ERROR', details);
    Object.setPrototypeOf(this, ConfigurationError.prototype);
  }
}

/**
 * Type guard to check if error is an AppError
 */
export function isAppError(error: any): error is AppError {
  return error instanceof AppError;
}

/**
 * Type guard to check if error is operational (expected) vs programming error
 */
export function isOperationalError(error: any): boolean {
  if (isAppError(error)) {
    return error.isOperational;
  }
  return false;
}

/**
 * Helper to create error response object
 */
export function formatErrorResponse(error: AppError) {
  return {
    success: false,
    error: {
      code: error.code,
      message: error.message,
      ...(error.details && { details: error.details }),
    },
  };
}

